var should = require('should');
var Structure = require('../lib/structure.js')


describe('www.brookstone.com', function() {

    before(function()  {
        this.timeout(5000);
    })


    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.brookstone.com/gifts?sz=24&start=624"
        }

        it('should return 24 products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(24);
                done()
            })
        });

        it('last product should be a East Side Collection - Reversible Sling Pet Carrier', function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: 'East Side Collection - Reversible Sling Pet Carrier',
                    price: '31.99',
                    url: 'http://www.brookstone.com/pd/east-side-collection---reversible-sling-pet-carrier/794184p.html',
                    image: 'http://www.brookstone.com/dis/dw/image/v2/AAYH_PRD/on/demandware.static/-/Sites-itemmaster_Brookstone/default/dw8c40d525/hi-res/794184p.jpg?sw=250 250w',
                    country: 'us'

                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

        it('first product should be Personalized Royce Leather RFID Blocking Fan Wallet', function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: 'Personalized Royce Leather RFID Blocking Fan Wallet',
                    price: '100.00',
                    url: 'http://www.brookstone.com/pd/personalized-royce-leather-rfid-blocking-fan-wallet/798590p.html',
                    image: 'http://www.brookstone.com/dis/dw/image/v2/AAYH_PRD/on/demandware.static/-/Sites-itemmaster_Brookstone/default/dw5af00c67/hi-res/798590p.jpg?sw=250 250w',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.brookstone.com/audio-tech/speakers/portable-speakers"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 24,
                    "pages": 2
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.brookstone.com/audio-tech"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Audio & Tech"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.brookstone.com/app-controlled-batman-batmobile-tumbler/313808p.html"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "App-Controlled Batman Batmobile Tumbler",
                    "price": "749.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://www.brookstone.com/dis/dw/image/v2/AAYH_PRD/on/demandware.static/-/Sites-itemmaster_Brookstone/default/dwe36c2a6f/hi-res/313808p.jpg?sw=500 500w',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });


    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should return false when product is out of stock', function(done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     })

    // })


});
