var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')


describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.buildabear.co.uk/shop/store/Cuddly-Pink-Teddy/productId=prod11240065",
            "html": cache.load("http://www.buildabear.co.uk/shop/store/Cuddly-Pink-Teddy/productId=prod11240065")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Cuddly Pink Teddy",
                    "price": "13.50",
                    "url": payload.url,
                    "inStock": "UNCHECKED",
                    "stockLevel": null,
                    "image": "http://www.buildabear.co.uk/ProductImages/BABW_UK/LR/421710LR.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

     describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.buildabear.co.uk/shop/search/Gifts/z/",
            "html": cache.load("http://www.buildabear.co.uk/shop/search/Gifts/z/")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Gifts",
                    "url":"http://www.buildabear.co.uk/shop/search/Gifts/z/"
                })           
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.buildabear.co.uk/shop/search/Stuffed_Animals/z/",
            "html": cache.load("http://www.buildabear.co.uk/shop/search/Stuffed_Animals/z/")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 96,
                    "pages": 3,
                    "url" : "http://www.buildabear.co.uk/shop/search/Stuffed_Animals/z/"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

     describe('Not a search page', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.buildabear.co.uk/shop/store/Online-Exclusive-Sonic-the-Hedgehog-Set/productId=prod12040036",
            "html": cache.load("http://www.buildabear.co.uk/shop/store/Online-Exclusive-Sonic-the-Hedgehog-Set/productId=prod12040036")
        }

        it('should return null items and pages', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": null,
                    "pages": null,
                    "url" : "http://www.buildabear.co.uk/shop/store/Online-Exclusive-Sonic-the-Hedgehog-Set/productId=prod12040036"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.buildabear.co.uk/accessories/play-accessories/eyewear",
            "html": cache.load("https://www.buildabear.co.uk/accessories/play-accessories/eyewear")
        }

        // Change to be the number of products on the page
        var x = 9;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Snorkel & Fin Set 4 pc.';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '10.00',
                    url: 'https://www.buildabear.co.uk/snorkel-and-fin-set-4-pc./425743.html?cgid=accessories-play-accessories-eyewear',
                    image: 'https://www.buildabear.co.uk/dw/image/v2/BBNG_PRD/on/demandware.static/-/Sites-buildabear-master/default/dwaa0dbb79/25743x.jpg?sw=600&sh=600&sm=fit',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Honey Girls Silver Glitter Sunglasses';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '2.80',
                    url: 'https://www.buildabear.co.uk/honey-girls-silver-glitter-sunglasses/424408.html?cgid=accessories-play-accessories-eyewear',
                    image: 'https://www.buildabear.co.uk/dw/image/v2/BBNG_PRD/on/demandware.static/-/Sites-buildabear-master/default/dw92eb5d95/24408x.jpg?sw=600&sh=600&sm=fit',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
