var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithDomainName', function () {

    this.timeout(5000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.burlington.com/b/Invicta-Angel-Womens-Crystal-Heart-Silver-Bracelet-Watch-548271532.aspx?h=69261",
            "html": cache.load("https://www.burlington.com/b/Invicta-Angel-Womens-Crystal-Heart-Silver-Bracelet-Watch-548271532.aspx?h=69261")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Invicta Angel Womens Crystal Heart Silver Bracelet Watch",
                    "price": "69.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://cdn.burlingtoncoatfactory.com/FileLibrary/fc88634eddf846fa9a664dc0523183a9/BCF548271532_L_2.jpg",
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "",
            "html": cache.load("")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.burlington.com/b/womens-watches-69261.aspx",
            "html": cache.load("https://www.burlington.com/b/womens-watches-69261.aspx")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Womens Watches",
                    "url":"https://www.burlington.com/b/womens-watches-69261.aspx"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Search page pagination', function () {

                var payload = {
                    "pageType": "pagination",
                    "url": "https://www.burlington.com/b/womens-watches-69261.aspx",
                    "html": cache.load("https://www.burlington.com/b/womens-watches-69261.aspx")
                }

                it('should be true when number of items and pages correct', function (done) {

                    Structure(payload, function (err, pagination) {
                        var expected = JSON.stringify({
                            "items": '',
                            "pages": ''
                        })
                        JSON.stringify(pagination).should.equal(expected);
                        done()
                    })
                });

            });


    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.burlington.com/b/womens-watches-69261.aspx",
            "html": cache.load("https://www.burlington.com/b/womens-watches-69261.aspx")
        }

        // Change to be the number of products on the page
        var x = 48;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Womens Crystal Mesh & Faux Leather Watch 7-Piece Set';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '19.99',
                    url: 'https://www.burlington.com/b/Womens-Crystal-Mesh-Faux-Leather-Watch-7-Piece-Set-545399271.aspx?h=69261',
                    image: 'https://cdn.burlingtoncoatfactory.com//FileLibrary/CAB199CC760D47AEBACAFE64A248C03D/BCF545399271_I_3.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Womens Crystal Bezel Chronograph Watch & Bracelet 10-Piece Set';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '19.99',
                    url: 'https://www.burlington.com/b/Womens-Crystal-Bezel-Chronograph-Watch-Bracelet-10-Piece-Set-547823091.aspx?h=69261',
                    image: 'https://cdn.burlingtoncoatfactory.com//FileLibrary/FCB6A1516A6D4BE1B5CB19DE7020519E/BCF547823091_I_4.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});


