    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.buybuybaby.com/store/category/gear-travel/baby-carriers/30539/?icid=intlhomepage_herocarousel2-intlhomepage&_requestid=4767669&_requestid=1001940&_requestid=6565679",
            "html": cache.load("https://www.buybuybaby.com/store/category/gear-travel/baby-carriers/30539/?icid=intlhomepage_herocarousel2-intlhomepage&_requestid=4767669&_requestid=1001940&_requestid=6565679")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(error,pagination) {
                var expected = JSON.stringify({
                    "items": 96,
                    "pages": 5,
                    "url":"https://www.buybuybaby.com/store/category/gear-travel/baby-carriers/30539/?icid=intlhomepage_herocarousel2-intlhomepage&_requestid=4767669&_requestid=1001940&_requestid=6565679"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.buybuybaby.com/store/category/gear-travel/baby-carriers/30539/?icid=intlhomepage_herocarousel2-intlhomepage&_requestid=4767669&_requestid=1001940&_requestid=6565679",
            "html": cache.load("https://www.buybuybaby.com/store/category/gear-travel/baby-carriers/30539/?icid=intlhomepage_herocarousel2-intlhomepage&_requestid=4767669&_requestid=1001940&_requestid=6565679")

        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err,category) {
                var expected = JSON.stringify({
                    "name": "Baby Carriers",
                    "url":"https://www.buybuybaby.com/store/category/gear-travel/baby-carriers/30539/?icid=intlhomepage_herocarousel2-intlhomepage&_requestid=4767669&_requestid=1001940&_requestid=6565679",

                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.buybuybaby.com/store/category/gear-travel/baby-carriers/30539/?icid=intlhomepage_herocarousel2-intlhomepage&_requestid=4767669&_requestid=1001940&_requestid=6565679",
            "html": cache.load("https://www.buybuybaby.com/store/category/gear-travel/baby-carriers/30539/?icid=intlhomepage_herocarousel2-intlhomepage&_requestid=4767669&_requestid=1001940&_requestid=6565679")
        }

        // Change to be the number of products on the page
        var x = 48;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err,result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Ergobaby™ Omni 360 Cool Air Mesh Baby Carrier';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err,result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '146.36',
                    url: 'https://www.buybuybaby.com/store/product/ergobaby-trade-omni-360-cool-air-mesh-baby-carrier/5125450?categoryId=30539',
                    image: 'https://s7d1.scene7.com/is/image/BedBathandBeyond/176280864674360p?$229$',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'BABYBJORN® Baby Carrier One Air in Silver Mesh';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err,result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '149.61',
                    url: 'https://www.buybuybaby.com/store/product/babybjorn-reg-baby-carrier-one-air-in-silver-mesh/1045838002?categoryId=30539',
                    image: 'https://s7d1.scene7.com/is/image/BedBathandBeyond/75462145838002p?$229$',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

        describe('Checking to see if no pagination works', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.buybuybaby.com/store/static/BabyAboutUs",
            "html": cache.load("https://www.buybuybaby.com/store/category/toys-learning/infant-toys/30527/")
        }

        it('should return 0 items and null pages', function (done) {

            Structure(payload, function(err,pagination) {
                var expected = JSON.stringify({
                    "items": 96,
                    "pages": 12,
                    "url":"https://www.buybuybaby.com/store/static/BabyAboutUs"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    });

