var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.c21stores.com/products/black-amina-real-fur-leather-lace-up-boots?color=Realfurblack&via=585c477d69702d7b4300007d%2C585c479069702d7b43000092"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Black Amina Real Fur & Leather Lace-Up Boots",
                    "price": "339.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://c21stores-weblinc.netdna-ssl.com/product_images/black-amina-real-fur-leather-lace-up-boots/001/582b2cfc69702d631a015603/detail.jpg?c=1479224572',
                    "country": 'us',
                    "description": 'Smooth leather and real fur upper, round toe, lace-up fastening, logo patch Wool interior lining, cushioned insole, rubber lug sole 1 1/8" heel Leather/Real Rabbit Fur upper, Textile lining, Man made balance Fur is dyed FoxFur Origin: ItalyMade in Italy Ladies'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    /*describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : ""
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });*/

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.c21stores.com/categories/shoes-boots"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 48,
                    "pages": 10
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.c21stores.com/categories/shoes-boots"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Women's Boots"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.c21stores.com/categories/shoes-boots"
        }

        // Change to be the number of products on the page
        var x = 48;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Black Amina Real Fur & Leather Lace-Up Boots';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '339.99',
                    url: 'https://www.c21stores.com/products/black-amina-real-fur-leather-lace-up-boots?color=Realfurblack&via=585c477d69702d7b4300007d%2C585c479069702d7b43000092',
                    image: 'https://c21stores-weblinc.netdna-ssl.com/product_images/black-amina-real-fur-leather-lace-up-boots/001/582b2cfc69702d631a015603/large_thumb.jpg?c=1479224572',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Black & White Margy Slip On Shoes';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '159.99',
                    url: 'https://www.c21stores.com/products/black-white-margy-slip-on-shoes?color=Black+White&via=585c477d69702d7b4300007d%2C585c479069702d7b43000092',
                    image: 'https://c21stores-weblinc.netdna-ssl.com/product_images/black-white-margy-slip-on-shoes/001/5857fc7369702d5058014def/large_thumb.jpg?c=1482161200',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
