var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.beautybrands.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.cafebritt.com/gourmet-coffee/costa-rican-dark-roast",
            "html": cache.load("https://www.cafebritt.com/gourmet-coffee/costa-rican-dark-roast")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Costa Rican Dark Roast",
                    "price": "12.95",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://www.cafebritt.com/siteimg/products/fullsize-tile/Costa-Rican-Dark-Roast-Whole-Bean.jpg?r=29246",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.cafebritt.com/gourmet-gifts/octagonal-chocolate-gift",
            "html": cache.load("https://www.cafebritt.com/gourmet-gifts/octagonal-chocolate-gift")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Octagonal Chocolate Gift",
                    "price": "12.95",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://www.cafebritt.com/siteimg/products/fullsize-tile/octogonal-chocolate-gift-red.jpg?r=29246",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.cafebritt.com/gourmet-coffee",
        "html": cache.load("https://www.cafebritt.com/gourmet-coffee")
    }

    // Change to be the number of products on the page
    var x = 33;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Heritage Blend';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '16.95',
                url: 'https://www.cafebritt.com/gourmet-coffee/heritage-blend',
                image: 'https://www.cafebritt.com/siteimg/products/tile/heritage-blend-2018-ground.jpg?r=29246',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Britt Espresso Valle Sagrado Espresso Capsules';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '8.50',
                url: 'https://www.cafebritt.com/gourmet-coffee/coffee-capsules/valle-sagrado-espresso-capsules',
                image: 'https://www.cafebritt.com/siteimg/products/tile/valle-sagrado-espresso-capsules.jpg?r=29246',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.cafebritt.com/gourmet-coffee",
            "html": cache.load("https://www.cafebritt.com/gourmet-coffee")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Gourmet Coffee",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
