describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.callawaygolf.com/accessories/travel-gear/?_ga=2.160457341.1125838594.1536707982-1447381704.1536707982",
        "html": cache.load("https://www.callawaygolf.com/accessories/travel-gear/?_ga=2.160457341.1125838594.1536707982-1447381704.1536707982")
    }

    // Change to be the number of products on the page
    var x = 23;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Clubhouse Small Duffle';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '80.00',
                url: 'https://www.callawaygolf.com/accessories/travel-gear/spr4524573.html',
                image: 'https://cdn-ap-ec.yottaa.net/58f0c35f32f01c6abd17a828/www.callawaygolf.com/v~4b.21/dw/image/v2/AADH_PRD/on/demandware.static/-/Sites-CGI-ItemMaster/en_US/v1536695670816/sits/travelgear-2016-clubhouse-small-duffle/travelgear-2016-clubhouse-small-duffle_6270___1.png?sw=270&sw=270&sfrm=png&yocs=d_&yoloc=ap',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = "Women's Callaway Uptown Shoe Bag";
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '60.00',
                url: 'https://www.callawaygolf.com/accessories/travel-gear/spr4747592.html',
                image: 'https://cdn-ap-ec.yottaa.net/58f0c35f32f01c6abd17a828/www.callawaygolf.com/v~4b.21/dw/image/v2/AADH_PRD/on/demandware.static/-/Sites-CGI-ItemMaster/en_US/v1536695670816/sits/travelgear-2018-uptown-shoe-bag-womens/travelgear-2018-uptown-shoe-bag-womens_6322___1.png?sw=270&sw=270&sfrm=png&yocs=d_&yoloc=ap',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.callawaygolf.com/accessories/travel-gear/?_ga=2.160457341.1125838594.1536707982-1447381704.1536707982",
            "html": cache.load("https://www.callawaygolf.com/accessories/travel-gear/?_ga=2.160457341.1125838594.1536707982-1447381704.1536707982")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Travel Gear",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
