var should = require('should');
var Structure = require('../lib/structure.js')

describe('www.campmor.com', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.campmor.com/h/outdoor-research-foray-jacket-mens"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Outdoor Research Foray Jacket - Men's",
                    "price": "172.96",
                    "url": 'https://www.campmor.com/h/outdoor-research-foray-jacket-mens',
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://static.campmor.com/wcsstore/Campmor/static/images/../images/items/main/34153blk.jpg',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.campmor.com/c/s/clothing/mens-clothing/mens-jackets-vests"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 28,
                    "pages": 8
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.campmor.com/c/s/gear/compasses"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Compasses"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.campmor.com/h/s/shop-by-sale/hot-deals/mens-clearance"
        }

        // Change to be the number of products on the page
        var x = 28;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Outdoor Research Foray Jacket - Men\'s';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '172.96',
                    url: 'https://www.campmor.com/h/outdoor-research-foray-jacket-mens',
                    image: 'https://static.campmor.com/wcsstore/Campmor/static/images/../images/items/main/34153dus.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Under Armour ColdGear Infrared EVO Crew - Men\'s';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '34.96',
                    url: 'https://www.campmor.com/h/under-armour-coldgear-infrared-evo-crew---mens',
                    image: 'https://static.campmor.com/wcsstore/Campmor/static/images/../images/items/main/M0438ste.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
