var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function() {

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.campusgifts.co.uk/product/boxer-fake-news",
            "html": cache.load("https://www.campusgifts.co.uk/product/boxer-fake-news")
        }

        it('should be true when product is in stock', function () {
            
            const inStockPage =  Structure(payload)    
            console.log(inStockPage)        
            inStockPage.name.should.be.equal("Fake News Hardback Book");
            inStockPage.price.should.be.equal("6.49");
            inStockPage.url.should.be.equal("https://www.campusgifts.co.uk/product/boxer-fake-news");
            inStockPage.inStock.should.be.equal(true);
            should(inStockPage.stockLevel).be.equal(null);
            inStockPage.inStock.should.be.equal(true);
            inStockPage.image.should.be.equal('https://www.campusgifts.co.uk/media/catalog/product/cache/3/image/750x750/9df78eab33525d08d6e5fb8d27136e95/b/o/boxer-yhu0398-fake-news-hardback-book-_2__1.jpg');
            inStockPage.country.should.be.equal('uk');            
         });
 
    });

    xdescribe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.campusgifts.co.uk/product/jellycat-medium-pink-bashful-bunny",
            "html": cache.load("http://www.campusgifts.co.uk/for-him/gadgets")
        }

        
    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.campusgifts.co.uk/for-him/gadgets",
            "html": cache.load("https://www.campusgifts.co.uk/for-him/gadgets")
        }

        it('should be true when number of items and pages correct', function () {

            var expected = JSON.stringify({
                "items": 90,
                "pages": 3,
                "url":"https://www.campusgifts.co.uk/for-him/gadgets"
            })

            const pagination = Structure(payload)

            JSON.stringify(pagination).should.equal(expected);
        });
    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.campusgifts.co.uk/for-him/gadgets",
            "html": cache.load("https://www.campusgifts.co.uk/for-him/gadgets")
        }

        it('should be true when number of items and pages correct', function () {

            var expected = JSON.stringify({
                "name": "For Him: Gadgets",
                "url":"https://www.campusgifts.co.uk/for-him/gadgets"
            })

            const category = Structure(payload)

            JSON.stringify(category).should.equal(expected);
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.campusgifts.co.uk/for-him/gadgets",
            "html": cache.load("https://www.campusgifts.co.uk/for-him/gadgets")
        }

        // Change to be the number of products on the page
        var x = 30;
        it('should return ' + x + ' numer of products', function () {
            let result = Structure(payload)
            result.length.should.equal(x);
        });

        var firstProductName = 'Critters Go For A Ride 100 Large Piece Jigsaw Puzzle';
        it('first product should be ' + firstProductName, function() {

            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '4.99',
                url: 'http://www.bitsandpieces.com/product/critters-go-for-a-ride-100-large-piece-jigsaw-puzzle-42777',
                image: 'https://s3.amazonaws.com/cdn.bitsandpieces.com/images/160/42777.jpg',
                country: 'us'
            })
            
            let result = Structure(payload)

            var first = result.shift()
            
            JSON.stringify(first).should.equal(expected)

        })

        var lastProductName = 'Soccer Sliding Wooden Puzzle';
        it('last product should be ' + lastProductName, function () {
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '14.99',
                url: 'http://www.bitsandpieces.com/product/soccer-sliding-wooden-puzzle',
                image: 'https://s3.amazonaws.com/cdn.bitsandpieces.com/images/160/43952.jpg',
                country: 'us'
            })

            const result = Structure(payload);

            var last = result.pop()
            JSON.stringify(last).should.equal(expected)
        })

    });
});
