var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.carolsdaughter.com/a%C3%A7ai-hydrating-foaming-face-wash/820645227361.html?cgid=skin-product-type-best-sellers"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Açai Hydrating Foaming Face Wash",
                    "price": "5.60",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://demandware.edgesuite.net/sits_pod22/dw/image/v2/AANG_PRD/on/demandware.static/-/Sites-cpd-cd-master-catalog/default/dw5b4cfccc/Carols-Daughter-Acai-Hydrating-Foaming-Face-Wash-K3915300-820645227361.jpg?sw=420&sh=420&sm=fit',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.carolsdaughter.com/a%C3%A7ai-hydrating-skincare-set/VS0263.html?cgid=skin-product-type-best-sellers"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Açai Hydrating Skincare Set",
                    "price": "25.00",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'http://demandware.edgesuite.net/sits_pod22/dw/image/v2/AANG_PRD/on/demandware.static/-/Sites-cpd-cd-master-catalog/default/dw13c36230/Carols-Daughter-Acai-Hydrating-Skincare-Set-VS0263.jpg?sw=420&sh=420&sm=fit',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.carolsdaughter.com/skin-product-type-best-sellers"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Best Sellers In Skin Care"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.carolsdaughter.com/skin-product-type-best-sellers"
        }

        // Change to be the number of products on the page
        var x = 9;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Açai Hydrating Foaming Face Wash';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '8.00',
                    url: 'http://www.carolsdaughter.com/a%C3%A7ai-hydrating-foaming-face-wash/820645227361.html?cgid=skin-product-type-best-sellers',
                    image: 'http://demandware.edgesuite.net/sits_pod22/dw/image/v2/AANG_PRD/on/demandware.static/-/Sites-cpd-cd-master-catalog/default/dw5b4cfccc/Carols-Daughter-Acai-Hydrating-Foaming-Face-Wash-K3915300-820645227361.jpg?sw=195&sh=195&sm=fit',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Açai Hydrating Skincare Set';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '25.00',
                    url: 'http://www.carolsdaughter.com/a%C3%A7ai-hydrating-skincare-set/VS0263.html?cgid=skin-product-type-best-sellers',
                    image: 'http://demandware.edgesuite.net/sits_pod22/dw/image/v2/AANG_PRD/on/demandware.static/-/Sites-cpd-cd-master-catalog/default/dw13c36230/Carols-Daughter-Acai-Hydrating-Skincare-Set-VS0263.jpg?sw=195&sh=195&sm=fit',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
