var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function () {

    this.timeout(10000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.carphonewarehouse.com/apple/iphone-7.html#!colour=gold&capacity=32GB&dealType=sf",
            "html": cache.load("https://www.carphonewarehouse.com/apple/iphone-7.html#!colour=gold&capacity=32GB&dealType=sf")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Apple iPhone 7 Gold 32 GB",
                    "price": "579.00",
                    "url": payload.url,
                    "inStock": "UNCHECKED",
                    "stockLevel": null,
                    "image": "https://media.carphonewarehouse.com/is/image/cpw2/iphone-7BLACK?$xl-standard$",
                    "country": "uk",
                    "description": "Looking for the best iPhone 7 deals, contracts and upgrades? We compare the UKâ€™s widest range of networks. And if you find it cheaper anywhere else, we'll match it and pay your first month."
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function () {

        var payload = {
            "pageType": "pagination",
            "url": "https://www.carphonewarehouse.com/special-offers/uks-biggest-mobile-sale-accessories.html",
            "html": cache.load("https://www.carphonewarehouse.com/special-offers/uks-biggest-mobile-sale-accessories.html")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function (err, pagination) {
                var expected = JSON.stringify({
                    "items": 12,
                    "pages": 3,
                    "url": "https://www.carphonewarehouse.com/special-offers/uks-biggest-mobile-sale-accessories.html"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.carphonewarehouse.com/mobiles/sim-free.html",
            "html": cache.load("https://www.carphonewarehouse.com/mobiles/sim-free.html")
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "SIM free phones",
                    "url": "https://www.carphonewarehouse.com/mobiles/sim-free.html"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.carphonewarehouse.com/mobiles/pay-as-you-go.html",
            "html": cache.load("https://www.carphonewarehouse.com/mobiles/pay-as-you-go.html")
        }

        // Change to be the number of products on the page
        var x = 9;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Apple iPhone 7 Apple iPhone 7';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '579.00',
                    url: 'www.carphonewarehouse.com/apple/iphone-7.html#!colour=gold&capacity=32GB&dealType=sf',
                    image: 'https://media.carphonewarehouse.com/is/image/cpw2/iphone-7?$finder-result$',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Samsung Galaxy A3 (2016) Samsung Galaxy A3 (2016)';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '249.99',
                    url: 'www.carphonewarehouse.com/samsung/galaxy-a3-2016.html#!colour=black&capacity=16GB&dealType=sf',
                    image: 'https://media.carphonewarehouse.com/is/image/cpw2/galaxy-A3-2016?$finder-result$',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
