var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.carters.com/carters-baby-boy-toy-and-gift/V_L33291H.html?dwvar_V__L33291H_size=NS&cgid=carters-baby-boy-toy-and-gift&dwvar_V__L33291H_color=Color"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Hippo Security Blanket",
                    "price": "17.00",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "http://www.carters.com/productimage/dw/image/v2/AAMK_PRD/on/demandware.static/-/Sites-carters_master_catalog/default/dw662c1037/hi-res/L33291H_Default.jpg?sw=470",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.carters.com/carters-baby-girl-one-pieces?navID=header"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Baby Girl One Piece"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.carters.com/carters-kid-boy-clearance?navID=header"
        }

        // Change to be the number of products on the page
        var x = 24;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Rocket Graphic Tee';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '7.99',
                    url: 'http://www.carters.com/carters-kid-boy-clearance/V_263H006.html?cgid=carters-kid-boy-clearance&dwvar_V__263H006_size=4&dwvar_V__263H006_color=Color',
                    image: 'http://www.carters.com/dw/image/v2/AAMK_PRD/on/demandware.static/-/Sites-carters_master_catalog/default/dw9a3ed153/hi-res/243H009_Default.jpg?sw=244',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Carter\'s Shark Slip-On Sneakers';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '16.99',
                    url: 'http://www.carters.com/carters-kid-boy-clearance/V_CS170515.html?dwvar_V__CS170515_color=Color&cgid=carters-kid-boy-clearance&dwvar_V__CS170515_size=5","image":"http://www.carters.com/dw/image/v2/AAMK_PRD/on/demandware.static/-/Sites-carters_master_catalog/default/dwee8e3bac/hi-res/CS170515_Default.jpg?sw=244',
                    image: 'http://www.carters.com/dw/image/v2/AAMK_PRD/on/demandware.static/-/Sites-carters_master_catalog/default/dw9728d889/hi-res/RS1818_Default.jpg?sw=244',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
