    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.catchyprice.com/collections/top-sellers"
        }

        // Change to be the number of products on the page
        var x = 35;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = '4 Piece Set Luxury Home Â® Bamboo Fiber 1800 Series Bed Sheet Sets';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '34.99',
                    url: 'https://www.catchyprice.com/collections/top-sellers/products/4-piece-set-bamboo-fiber-1800-series-bed-sheet-sets',
                    image: 'https://cdn.shopify.com/s/files/1/1706/0207/products/6-Piece-Set--Super-Soft-1800-Series-Bamboo-Fiber-Bed-Sheets_large.jpg?v=1492726711',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = '6-Pocket Maximum Storage Armrest Organizer with Large Tabletop - Black';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '7.99',
                    url: 'https://www.catchyprice.com/collections/top-sellers/products/6-pocket-maximum-storage-armrest-organizer-with-large-tabletop-black',
                    image: 'https://cdn.shopify.com/s/files/1/1706/0207/products/0002686_6-pocket-maximum-storage-armrest-organizer-with-large-tabletop-black-1_large.jpg?v=1492204894',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });



