var should = require('should');
var Structure = require('../lib/structure.js')

describe('www.chasing-fireflies.com', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.chasing-fireflies.com/38-22-personalized-mermaid-doll/toys/only-at-chasing-fireflies/212882"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "38\" personalized mermaid doll",
                    "price": "78.00",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://chasingfireflies.scene7.com/is/image/ChasingFireflies/54551_main?$P_Main$',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    //});

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.chasing-fireflies.com/category/girls/dresses.do?currentPage=1"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 36,
                    "pages": 5
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

     describe('Not a search page', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.chasing-fireflies.com/product/girls-glitter-heart-pjs.do?sortby=ourPicksAscend&refType=&from=fn&cl1&ecList=7&ecCategory=103619"
        }

        it('should return null items and pages', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": null,
                    "pages": null
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.chasing-fireflies.com/sale/baby-sale/#w=*&af=cat2:clearance_baby cat1:clearance pagetype:products"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Clearance"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.chasing-fireflies.com/category/girls/dresses.do?currentPage=1"
        }

        // Change to be the number of products on the page
        var x = 36;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Girls Smocked Romantic Dress';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '189.00',
                    url: 'https://www.chasing-fireflies.com/product/girls-smocked-romantic-dress.do?sortby=ourPicksAscend&refType=&from=fn',
                    image: 'https://ii.chadwicks.com/fcgi-bin/iipsrv.fcgi?FIF=/images/chadwicks/source/products/856422_sag_1.tif&wid=220&cvt=jpeg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Girls Bohemian Chiffon Dress';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '69.00',
                    url: 'https://www.chasing-fireflies.com/product/girls-bohemian-chiffon-dress.do?sortby=ourPicksAscend&refType=&from=fn',
                    image: 'https://ii.chadwicks.com/fcgi-bin/iipsrv.fcgi?FIF=/images/chadwicks/source/products/853626_pin_1.tif&wid=220&cvt=jpeg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
