var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.chicos.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.chicos.com/store/product/basic+denim+jacket/570242112?color=001&catId=cat40001",
            "html": cache.load("https://www.chicos.com/store/product/basic+denim+jacket/570242112?color=001&catId=cat40001")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Basic Denim Jacket",
                    "price": "99.00",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://www.chicos.com/Product_Images/570242112_001_large.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.chicos.com/store/product/lace+trim+peasant+top/570200241",
            "html": cache.load("https://www.chicos.com/store/product/lace+trim+peasant+top/570200241")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Lace Trim Peasant Top",
                    "price": null,
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://www.chicos.com/Product_Images/570200241_177_large.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.chicos.com/store/category/jackets/cat40001/",
        "html": cache.load("https://www.chicos.com/store/category/jackets/cat40001/")
    }

    // Change to be the number of products on the page
    var x = 70;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Sueded Lace-Up Sleeve Jacket';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '149.00',
                url: 'https://www.chicos.com/store/product/sueded+laceup+sleeve+jacket/570242114?color=4539&catId=cat40001',
                image: 'https://www.chicos.com/Product_Images/570242114_4539.jpg?resize=300px:*',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Travelers Collection Toggle-Closure Jacket';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '129.00',
                url: 'https://www.chicos.com/store/product/travelers+collection+toggleclosure+jacket/570244078?color=001&catId=cat40001',
                image: 'https://www.chicos.com/Product_Images/570244078_001.jpg?resize=300px:*',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.chicos.com/store/category/jackets/cat40001/",
            "html": cache.load("https://www.chicos.com/store/category/jackets/cat40001/")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Jackets",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
