var should = require('should');
var Structure = require('../lib/structure.js');
const expect = require('chai').expect;
var sku = require('../lib/sku');
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.christianbook.com/reindeer-mini-wood-puzzle/pd/76402?event=Bargains|1005403",
            "html": cache.load("https://www.christianbook.com/reindeer-mini-wood-puzzle/pd/76402?event=Bargains|1005403")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Press & Go Activity Plane",
                    "price": "19.99",
                    "url": "https://www.christianbook.com/press-go-activity-plane/pd/3476967?event=Kids|1002521",
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://g.christianbook.com/dg/product/cbd/f400/3476967.jpg',
                    "country": 'us',
                    "barcode": '090543476967',
                    "sku": '3476947'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.christianbook.com/page/kids/shop-by-age/ages-35/ages-35-classic-books?event=Kids|1000483"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 24,
                    "pages": 5
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.christianbook.com/page/kids/toys/toys-for-boys/boy-toys-ages-02?event=Kids|1002521"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Toys for Baby & Toddler Boys"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    })

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.christianbook.com/page/kids/toys/toys-for-boys/boy-toys-ages-02?event=Kids|1002521",
            "html": cache.load("https://www.christianbook.com/page/kids/toys/toys-for-boys/boy-toys-ages-02?event=Kids|1002521")
        }

        // Change to be the number of products on the page
        var x = 13;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Press & Go Activity Plane';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '19.99',
                    url: 'http://www.christianbook.com/press-go-activity-plane/pd/3476967?event=Kids|1006435',
                    image: 'http://g.christianbook.com/dg/product/cbd/w163/3476967.jpg',
                    country: 'us',
                    sku: '3476967'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'John Deere, Roll n Go Flashlight';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '12.99',
                    url: 'http://www.christianbook.com/john-deere-roll-n-go-flashlight/pd/50835X?event=Kids|1006435',
                    image: 'http://g.christianbook.com/dg/product/cbd/w163/50835x.jpg',
                    country: 'us',
                    sku: '50835X'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

    describe('sku regex matching', () => {
        it('should only accept a sku that matches the correct pattern', (done) => {
            let goodSkus = [
                '088862', '59291X', '084314', '245425', '421190', '92681EB', '546991', '1418L', '831128', '370460'
            ];
            goodSkus.map((thisSku) => {
                let testUrl = 'https://www.christianbook.com/strength-dignity-hooded-sleeve-heather-large/pd/' + thisSku + '?event=Clothing-and-Accessories|1000447';
                let result = sku(testUrl);
                result.should.equal(thisSku);
            });
            done();
        });

        it('should not accept a sku with an invalid pattern', (done) => {
            let badSkus = [
                '59291;session', '084314,id=12', '245425&n=2'
            ];
            badSkus.map((thisSku) => {
                let testUrl = 'https://www.christianbook.com/strength-dignity-hooded-sleeve-heather-large/pd/' + thisSku + '?event=Clothing-and-Accessories|1000447';
                let result = sku(testUrl);
                expect(result).to.equal(null);
            });
            done();
        });

    });

});
