var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithDomainName', function () {

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.claires.com/geometric-marble-phone-case-210788M.html?cgid=168&lang=en_GB",
            "html": cache.load("https://www.claires.com/geometric-marble-phone-case-210788M.html?cgid=168&lang=en_GB")
        }

        it('should be true when product is in stock', function () {
            
            const inStockPage =  Structure(payload)   
            console.log(inStockPage)         
            inStockPage.name.should.be.equal("Geometric Marble Phone Case");
            inStockPage.price.should.be.equal("4.80");
            inStockPage.url.should.be.equal("https://www.claires.com/geometric-marble-phone-case-210788M.html?cgid=168&lang=en_GB");
            inStockPage.inStock.should.be.equal(true);
            should(inStockPage.stockLevel).be.equal(null);
            inStockPage.image.should.be.equal('https://www.claires.com/dw/image/v2/BBTK_PRD/on/demandware.static/-/Sites-master-catalog/default/dwe9f65db3/images/hi-res/93699_1.jpg?sw=2000&sh=2000&sm=fit');
            inStockPage.country.should.be.equal('us');            
         });
 
 
    });

    xdescribe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "",
            "html": cache.load("")
        }

        
        it('should be true when product is in stock', function () {
            
            const inStockPage =  Structure(payload)            
            inStockPage.name.should.be.equal("");
            inStockPage.price.should.be.equal("");
            inStockPage.url.should.be.equal("");
            inStockPage.inStock.should.be.equal(true);
            should(inStockPage.stockLevel).be.equal(null);
            inStockPage.image.should.be.equal('');
            inStockPage.country.should.be.equal('');            
         });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.claires.com/tech/phone-cases/samsung-cases/?lang=en_GB",
            "html": cache.load("https://www.claires.com/tech/phone-cases/samsung-cases/?lang=en_GB")
        }
        it('should be true when number of items and pages correct', function () {

            var expected = JSON.stringify({
                "name": "Samsung GalaxyÂ® Cases",
                "url":"https://www.claires.com/tech/phone-cases/samsung-cases/?lang=en_GB"
            })

            const pagination = Structure(payload)

            JSON.stringify(pagination).should.equal(expected);
        });

    });

    describe('Search page pagination', function () {

                var payload = {
                    "pageType": "pagination",
                    "url": "https://www.claires.com/tech/phone-cases/samsung-cases/?lang=en_GB",
                    "html": cache.load("https://www.claires.com/tech/phone-cases/samsung-cases/?lang=en_GB")
                }

                it('should be true when number of items and pages correct', function () {

                    var expected = JSON.stringify({
                        "items": "22",
                        "pages":"",
                        "url":"https://www.claires.com/tech/phone-cases/samsung-cases/?lang=en_GB"
                    })
        
                    const category = Structure(payload)
        
                    JSON.stringify(category).should.equal(expected);
                });        

            });


    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.claires.com/tech/phone-cases/samsung-cases/?lang=en_GB",
            "html": cache.load("https://www.claires.com/tech/phone-cases/samsung-cases/?lang=en_GB")
        }

        // Change to be the number of products on the page
        var x = 22;
        it('should return ' + x + ' numer of products', function () {
            let result = Structure(payload)
            result.length.should.equal(x);
        });

        var firstProductName = 'Marley Pug Pop Over Phone Case';
        it('first product should be ' + firstProductName, function() {

            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '4.80',
                url: 'https://www.claires.com/marley-pug-pop-over-phone-case-229291M.html?cgid=168&lang=en_GB',
                image: 'https://www.claires.com/dw/image/v2/BBTK_PRD/on/demandware.static/-/Sites-master-catalog/default/dw3b322a6b/images/hi-res/98823_1.jpg?sw=290&sh=290&sm=fit',
                country: 'us'
            })
            
            let result = Structure(payload)

            var first = result.shift()
            
            JSON.stringify(first).should.equal(expected)

        })

        var lastProductName = 'Rainbow Marble with Gold Foil Flakes Phone Case';
        it('last product should be ' + lastProductName, function () {
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '4.80',
                url: 'https://www.claires.com/rainbow-marble-with-gold-foil-flakes-phone-case-230461.html?cgid=168&lang=en_GB',
                image: 'https://www.claires.com/dw/image/v2/BBTK_PRD/on/demandware.static/-/Sites-master-catalog/default/dw7ad4ce93/images/hi-res/23509_1.jpg?sw=290&sh=290&sm=fit',
                country: 'us'
            })

            const result = Structure(payload);

            var last = result.pop()
            JSON.stringify(last).should.equal(expected)
        })
    }); 
});