    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.clarksusa.com/us/c/mens-shoes"
        }

        // Change to be the number of products on the page
        var x = 18;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Franson Plain Tan Leather';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '115.00',
                    url: 'http://www.clarksusa.com/us/mens/mens-shoes/mens-dress-casual-shoes/franson-plain-tan-leather/p/26115730',
                    image: 'https://clarks.scene7.com/is/image/clarks/26115730_AZ?$prod_list$',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Bampton Limit Black Hi-Shine Leather';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '79.99',
                    url: 'http://www.clarksusa.com/us/mens/mens-shoes/mens-dress-shoes/bampton-limit-black-hi-shine-leather/p/26119791',
                    image: 'https://clarks.scene7.com/is/image/clarks/26119791_AZ?$prod_list$',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.clarksusa.com/us/c/mens-shoes"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Shoes"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });


