var should = require('should');
var Structure = require('../lib/structure.js')

describe('www.clasohlson.com', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.clasohlson.com/uk/Clas-Ohlson-AA-LR6-Alkaline-Battery/36-5487-10"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Clas Ohlson AA/LR6 Alkaline Battery",
                    "price": "1.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://images.clasohlson.com/medias/sys_master/9272327012382.jpg',
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });
    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.clasohlson.com/uk/b/Electrical/Batteries-&-Chargers"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Batteries & Chargers"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.clasohlson.com/uk/b/Electrical/Batteries-&-Chargers"
        }

        // Change to be the number of products on the page
        var x = 30;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = '12V power supply unit for cool boxes';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '15.99',
                    url: 'http://www.clasohlson.com/uk/12V-power-supply-unit-for-cool-boxes/31-1567',
                    image: 'http://images.clasohlson.com/medias/sys_master/9132664193054.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = '500 W Power Converter/Inverter';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '59.99',
                    url: 'http://www.clasohlson.com/uk/500-W-Power-Converter-Inverter/18-2532',
                    image: 'http://images.clasohlson.com/medias/sys_master/9001985048606.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
