var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')


describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.comacotoys.com/Marvel-Groot-Plush-12?nh=3573646",
            "html" : cache.load("http://www.comacotoys.com/Marvel-Groot-Plush-12?nh=3573646")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Marvel Groot Plush 12\"",
                    "price": "14.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://cdn0.fast-serve.net/cdn/comacodirect/Marvel-Groot-Plush-12-quot_400_7M43E.jpg',
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.comacotoys.com/Super-Heroes/",
            "html" : cache.load("http://www.comacotoys.com/Super-Heroes")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "pages": 3
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

     describe('Not a search page', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.comacotoys.com/Captain-America-Avengers-Wall-Crawler",
            "html" : cache.load("http://www.comacotoys.com/Captain-America-Avengers-Wall-Crawler")
        }

        it('should return null items and pages', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "pages": null,
                    "url" : payload.url
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.comacotoys.com/Super-Heroes/",
            "html" : cache.load("http://www.comacotoys.com/Super-Heroes/")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Marvel Super Heroes",
                    "url" : payload.url
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.comacotoys.com/Super-Heroes/",
            "html" : cache.load("http://www.comacotoys.com/Super-Heroes/")
        }

        // Change to be the number of products on the page
        var x = 16;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Groot Guardians of the Galaxy Flying Heroes';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '14.99',
                    url: 'http://www.comacotoys.com/Groot-Guardians-of-the-Galaxy-Flying-Heroes',
                    image: 'http://cdn2.fast-serve.net/cdn/comacodirect/Groot-Guardians-of-the-Galaxy-Flying-Heroes_285_8DEKE.jpg?height=285&width=285&scale=both&quality=40',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Spiderman Wall Crawler Red & Black';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '13.99',
                    url: 'http://www.comacotoys.com/Spiderman-Wall-Crawler-Red-Black',
                    image: 'http://cdn0.fast-serve.net/cdn/comacodirect/Spiderman-Wall-Crawler-Red-amp-Black_285_8OT8G.jpg?height=285&width=285&scale=both&quality=40',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
