var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.conrad-electronic.co.uk', function () {

    this.timeout(5000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.conrad-electronic.co.uk/ce/en/product/995167",
            "html": cache.load("https://www.conrad-electronic.co.uk/ce/en/product/995167")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Monitor wall mount 58,4 cm (23\") - 106,7 cm (42\") Swivelling/tiltable Manhatta",
                    "price": "42.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://www.conrad-electronic.co.uk/medias/global/ce/9000_9999/9900/9950/9951/995167_RB_00_FB.EPS_400.jpg',
                    "country": 'uk',
                    "sku": "995167"
                });
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

        describe('Search Page', function () {

            var payload = {
                "pageType": "search",
                "url": "https://www.conrad-electronic.co.uk/ce/en/overview/3600016/Chess-Computers-Misc-Consoles",
                "html": cache.load("https://www.conrad-electronic.co.uk/ce/en/overview/3600016/Chess-Computers-Misc-Consoles")
            };

            // Change to be the number of products on the page
            var x = 6;
            it('should return ' + x + ' products', function (done) {
                Structure(payload, function (err, result) {
                    result.length.should.equal(x);
                    done();
                })
            });

            var firstProductName = 'Chess computer Millennium Europe Chess Master II';
            it('first product should be ' + firstProductName, function (done) {
                Structure(payload, function (err, result) {
                    var first = result.shift()
                    var expected = JSON.stringify({
                        barcode: null,
                        name: firstProductName,
                        price: '30.99',
                        url: 'https://www.conrad-electronic.co.uk/ce/en/product/1301868',
                        image: 'https://www.conrad-electronic.co.uk/medias/global/ce/3000_3999/3100/3110/3116/1301868_RB_00_FB.EPS_250.jpg',
                        country: 'uk',
                        sku: "1301868"
                    })
                    JSON.stringify(first).should.equal(expected)
                    done()
                })

            })

            var lastProductName = 'Chess computer Millennium Mains Adaptor for ChessGenius';
            it('last product should be ' + lastProductName, function (done) {
                Structure(payload, function (err, result) {
                    var last = result.pop()
                    var expected = JSON.stringify({
                        barcode: null,
                        name: lastProductName,
                        price: '21.99',
                        url: 'https://www.conrad-electronic.co.uk/ce/en/product/1406541',
                        image: 'https://www.conrad-electronic.co.uk/medias/global/ce/7000_7999/7900/7950/7954/1406541_ZB_00_FB.EPS_250.jpg',
                        country: 'uk',
                        sku: "1406541",
                    })
                    JSON.stringify(last).should.equal(expected)
                    done()
                });
            })
            // describe('Out of Stock Page', function() {

            //     var payload = {
            //         "pageType" : "product",
            //         "url" : "http://www.conrad-electronic.co.uk/ce/en/product/1268748/Aerotec-2010161?ref=list"
            //     }

            //     it('should return false when product is out of stock', function(done) {

            //         Structure(payload, function(inStockPage) {
            //             var expected = JSON.stringify({
            //                 "name": "Aerotec 2010161",
            //                 "price": "24.99",
            //                 "url": null,
            //                 "inStock": false,
            //                 "stockLevel": null
            //             })
            //             JSON.stringify(inStockPage).should.equal(expected);
            //             done()
            //         })
            //     })

            // })

            describe('Search page pagination', function () {

                var payload = {
                    "pageType": "pagination",
                    "url": "http://www.conrad-electronic.co.uk/ce/en/overview/0413021/PC-Monitor-Stands-Brackets",
                    "html": cache.load("http://www.conrad-electronic.co.uk/ce/en/overview/0413021/PC-Monitor-Stands-Brackets")
                }

                it('should be true when number of items and pages correct', function (done) {

                    Structure(payload, function (err, pagination) {
                        var expected = JSON.stringify({
                            "items": 36,
                            "pages": 4,
                            "url": "http://www.conrad-electronic.co.uk/ce/en/overview/0413021/PC-Monitor-Stands-Brackets"
                        })
                        JSON.stringify(pagination).should.equal(expected);
                        done()
                    })
                });

            })

            describe('Not a search page', function () {

                var payload = {
                    "pageType": "pagination",
                    "url": "http://www.conrad-electronic.co.uk/ce/en/product/416081/3x-Monitor-desk-mount-330-cm-13-610-cm-24-Swivellingtiltable-Swivelling-SpeaKa-Professional?ref=list",
                    "html": cache.load("http://www.conrad-electronic.co.uk/ce/en/product/416081/3x-Monitor-desk-mount-330-cm-13-610-cm-24-Swivellingtiltable-Swivelling-SpeaKa-Professional?ref=list")
                }

                it('should return null items and pages', function (done) {

                    Structure(payload, function (err, pagination) {
                        var expected = JSON.stringify({
                            "items": null,
                            "pages": null,
                            "url": "http://www.conrad-electronic.co.uk/ce/en/product/416081/3x-Monitor-desk-mount-330-cm-13-610-cm-24-Swivellingtiltable-Swivelling-SpeaKa-Professional?ref=list"
                        })
                        JSON.stringify(pagination).should.equal(expected);
                        done()
                    })
                });

            });
        });
    });
});
