var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.coolshop.co.uk/product/star-wars-ezra-bridger-12-inch-figure-a8546/AM3Z2W",
            "html" : cache.load("https://www.coolshop.co.uk/product/star-wars-ezra-bridger-12-inch-figure-a8546/AM3Z2W")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Star Wars - Ezra Bridger - 12 Inch figure (A8546)",
                    "price": "15.94",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://img-cache.coolshop.com/df869d7a-1ac8-4459-915a-516ea95188f4/ezra.jpg?width=580',
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });
    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.coolshop.co.uk/toys/figures/star+wars",
            "html" : cache.load("https://www.coolshop.co.uk/toys/figures/star+wars")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Star Wars Â· Figures",
                    "url": payload.url
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.coolshop.co.uk/toys/electronic-toys/",
            "html" : cache.load("https://www.coolshop.co.uk/toys/electronic-toys/")
        }

        // Change to be the number of products on the page
        var x = 100;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Gro Clock';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '29.99',
                    url: 'https://www.coolshop.co.uk/product/gro-clock/AC9WR7/',
                    image: 'https://img-cache.coolshop.com/d1978866-4fc9-462e-9b23-550cc081ec2e/gro-clock.jpeg?width=220&height=240',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Battroborg Dent Single Pack Gold';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '29.99',
                    url: 'https://www.coolshop.co.uk/product/battroborg-dent-single-pack-gold/AF37RN/',
                    image: 'https://img-cache.coolshop.com/3ebc67af-3f57-4c84-9545-e22ee2425f32/battroborg-dent-single-pack-gold.jpg?width=220&height=240',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
