var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithDomainName', function () {

    this.timeout(5000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.cpooutlets.com/karcher-1-106-113-0-k1700-cube-1-700-psi-1-2-gpm-electric-pressure-washer/karn1.106-113.0,default,pd.html",
            "html": cache.load("https://www.cpooutlets.com/karcher-1-106-113-0-k1700-cube-1-700-psi-1-2-gpm-electric-pressure-washer/karn1.106-113.0,default,pd.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Karcher 1.106-113.0 K1700 Cube 1,700 PSI 1.2 GPM Electric Pressure Washer",
                    "price": "128.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://www.cpooutlets.com/on/demandware.static/-/Sites-cpo-master-catalog/default/dwd8b091d9/product_media/kar/karn1.106-113.0/images/xlarge/karn1.106-113.0.jpg",
                    "country": 'us',
                "sku":'KARN1.106-113.0'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "",
            "html": cache.load("")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.cpooutlets.com/outdoor-pressure-washers/outdoor-pressure-washers,default,sc.html",
            "html": cache.load("https://www.cpooutlets.com/outdoor-pressure-washers/outdoor-pressure-washers,default,sc.html")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Pressure Washers",
                    "url": 'https://www.cpooutlets.com/outdoor-pressure-washers/outdoor-pressure-washers,default,sc.html'
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Search page pagination', function () {

                var payload = {
                    "pageType": "pagination",
                    "url": "",
                    "html": cache.load("")
                }

                it('should be true when number of items and pages correct', function (done) {

                    Structure(payload, function (err, pagination) {
                        var expected = JSON.stringify({
                            "items": '',
                            "pages": ''
                        })
                        JSON.stringify(pagination).should.equal(expected);
                        done()
                    })
                });

            });


    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.cpooutlets.com/outdoor-pressure-washers/outdoor-pressure-washers,default,sc.html",
            "html": cache.load("https://www.cpooutlets.com/outdoor-pressure-washers/outdoor-pressure-washers,default,sc.html")
        }

        // Change to be the number of products on the page
        var x = 36;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Dewalt DXPW4240 4,200 PSI 4.0 GPM Gas Pressure Washer with Honda Engine (Certified)';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '1137.99',
                    url: 'https://www.cpooutlets.com/dewalt-dxpw4240-4-200-psi-4-0-gpm-gas-pressure-washer-with-honda-engine--certified-/dewcdxpw4240,default,pd.html',
                    image: 'https://www.cpooutlets.com/on/demandware.static/-/Sites-cpo-master-catalog/default/dw6954e372/product_media/dew/dewcdxpw4240/images/medium/dewcdxpw4240.jpg',
                    country: 'us',
                    sku: 'dewcdxpw4240'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Briggs & Stratton 20654A 13.75 Amp 1.3 GPM Electric Pressure Washer with Instant Start/Stop System';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '132.99',
                    url: 'https://www.cpooutlets.com/briggs-and-stratton-20654a-13-75-amp-1-3-gpm-electric-pressure-washer-with-instant-start-stop-system/bgsn20654,default,pd.html',
                    image: 'https://www.cpooutlets.com/on/demandware.static/-/Sites-cpo-master-catalog/default/dw320ecfda/product_media/bgs/bgsn20654/images/medium/bgsn20654.jpg',
                    country: 'us',
                    sku: 'bgsn20654'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});


