var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.craftcompany.co.uk', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.craftcompany.co.uk/callebaut-belgian-chocolate-milk-1kg.html",
            "html": cache.load("https://www.craftcompany.co.uk/callebaut-belgian-chocolate-milk-1kg.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Callebaut Belgian Chocolate - Milk - 1kg",
                    "price": "10.75",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://www.craftcompany.co.uk/media/catalog/product/cache/1/image/9df78eab33525d08d6e5fb8d27136e95/m/i/milk_callebaut.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.craftcompany.co.uk/chocolate-genoese-sponge-cake-square-6.html",
            "html": cache.load("https://www.craftcompany.co.uk/chocolate-genoese-sponge-cake-square-6.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Chocolate Genoese Sponge Cake –Square – 6”",
                    "price": "9.65",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://www.craftcompany.co.uk/media/catalog/product/cache/1/image/9df78eab33525d08d6e5fb8d27136e95/s/s/ss-choccake-sq_5.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.craftcompany.co.uk/chocolate-cookies-and-cake-pops/chocolate-making.html",
        "html": cache.load("https://www.craftcompany.co.uk/chocolate-cookies-and-cake-pops/chocolate-making.html")
    }

    // Change to be the number of products on the page
    var x = 64;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Belcolade Belgian Chocolate - White - 1kg';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '7.85',
                url: 'https://www.craftcompany.co.uk/belcolade-belgian-chocolate-white-1kg.html',
                image: 'https://www.craftcompany.co.uk/media/catalog/product/cache/1/small_image/210x/9df78eab33525d08d6e5fb8d27136e95/b/e/belcolade-white-chocolate-1kg.jpg',
                country: 'uk'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Flower & Wildlife Chocolate Mould - Set of 2';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '0.85',
                url: 'https://www.craftcompany.co.uk/flower-wildlife-chocolate-mould-set-of-2.html',
                image: 'https://www.craftcompany.co.uk/media/catalog/product/cache/1/small_image/210x/9df78eab33525d08d6e5fb8d27136e95/k/c/kccmflobugpk2.jpg',
                country: 'uk'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.craftcompany.co.uk/chocolate-cookies-and-cake-pops/chocolate-making.html",
            "html": cache.load("https://www.craftcompany.co.uk/chocolate-cookies-and-cake-pops/chocolate-making.html")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Chocolate Making",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
