var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.crocs.co.uk', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.crocs.co.uk/p/womens-crocs-isabella-block-heels/204002.html?cgid=sale&cid=02S#&origin=category&srule=Sale-Phase2Phase3&start=32&sz=36&prefn1=gender&prefv1=Women&prefn2=variationSize&prefv2=&prefn3=refinementColor&prefv3=&prefn4=fitGuideFit&prefv4=&prefn5=lifestyle&prefv5=",
            "html": cache.load('https://www.crocs.co.uk/p/womens-crocs-isabella-block-heels/204002.html?cgid=sale&cid=02S#&origin=category&srule=Sale-Phase2Phase3&start=32&sz=36&prefn1=gender&prefv1=Women&prefn2=variationSize&prefv2=&prefn3=refinementColor&prefv3=&prefn4=fitGuideFit&prefv4=&prefn5=lifestyle&prefv5=')
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Women's Crocs Isabella Block Heels",
                    "price": "34.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://images.crocs.com/is/image/Crocs/thumbnail/Volt-Green-and-White-Crocband-Flip-_11033_394_IS.jpg',
                    "country": 'uk',
                    "description": "Ready for date night? These heels sure are! Soft, flexible straps with buckles at the ankles offer a secure fit, while the soft Croslite™ foam underfoot delivers comfort with every step. 50-mm heels offer a bit of lift without taking you out of your comfort zone. Women's Crocs Isabella Block Heels Details:Feminine proportions and shape Soft TPU translucent straps Beautifully embellished with braided straps Adjustable buckle On-trend, 50-mm block heel Dual Crocs Comfort™: original Croslite™ foam cushion Kevin from undisclosed \"A very comfortable shoe!! Love them!!!\" Standard No extra room; fits comfortably snug. Dual Crocs Comfort™ Deep cushion: Feels soft with all day support."
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.crocs.co.uk/p/mens-citilane-roka-camo-slip-ons/204526.html?cgid=citilane&cid=960",
            "html": cache.load('https://www.crocs.co.uk/p/mens-citilane-roka-camo-slip-ons/204526.html?cgid=citilane&cid=960')
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Men's CitiLane Roka Camo Slip-Ons",
                    "price": "",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'https://images.crocs.com/is/image/Crocs/thumbnail/Volt-Green-and-White-Crocband-Flip-_11033_394_IS.jpg',
                    "country": 'uk',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Search page pagination', function() {

    //     var payload = {
    //         "pageType" : "pagination",
    //         "url" : "http://www.crocs.com/c/boys/footwear"
    //     }

    //     it('should be true when number of items and pages correct', function (done) {

    //         Structure(payload, function(pagination) {
    //             var expected = JSON.stringify({
    //                 "items": 48,
    //                 "pages": 3
    //             })
    //             JSON.stringify(pagination).should.equal(expected);
    //             done()
    //         })
    //     });

    // })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.crocs.co.uk/c/women/footwear",
            "html": cache.load('https://www.crocs.co.uk/c/women/footwear')
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Women's Shoes and Accessories",
                    "url": payload.url
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.crocs.co.uk/c/women/footwear",
            "html": cache.load("https://www.crocs.co.uk/c/women/footwear")
        }

        // Change to be the number of products on the page
        var x = 36;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Crocband™ Clog';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '',
                    url: 'https://www.crocs.co.uk/p/crocband-clog/11016.html?cgid=women-footwear&cid=3R6#start=13',
                    image: 'https://images.crocs.com/is/image/Crocs/thumbnail/New-MintandTropical-Teal-Crocband-Clog-_11016_3R6_IS.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Bayaband Slide';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '20.99',
                    url: 'https://www.crocs.co.uk/p/bayaband-slide/205392.html?cgid=women-footwear&cid=4CC#start=48',
                    image: 'https://images.crocs.com/is/image/Crocs/thumbnail/Navy-and-Pepper-Bayaband-Slide-_205392_4CC_IS.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
