var should = require('should');
var Structure = require('../lib/structure.js')

describe('www.currys.co.uk', function() {

    before(function()  {
        this.timeout(5000);
    })

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.currys.co.uk/gbuk/cameras-and-camcorders/digital-cameras/dslr-and-compact-system-cameras/nikon-d5300-dslr-camera-with-18-55-mm-f-3-5-5-6-zoom-lens-21802349-pdt.html"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": 'NIKON D5300 DSLR Camera with 18-55 mm f/3.5-5.6 Zoom Lens',
                    "price": "479.00",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://brain-images.cdn.dixons.com/9/4/21802349/u_21802349.jpg',
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.currys.co.uk/gbuk/computing-accessories/computer-accessories/headsets-and-microphones/tritton-swarm-2-0-wireless-gaming-headset-10139036-pdt.html?srcid=369&xtor=AL-1&cmpid=aff~!!!sitenamecm!!!~!!!promotypecm!!!&istCompanyId=bec25c7e-cbcd-460d-81d5-a25372d2e3d7&istItemId=atrlqtatx&istBid=t"
        }

        it('should return false when product is out of stock', function(done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "TRITTON Swarm 2.0 Wireless Gaming Headset",
                    "price": "29.97",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "http://brain-images.cdn.dixons.com/6/3/10139036/u_10139036.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        })

    })


    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.currys.co.uk/gbuk/freestanding-microwaves/cooking/microwaves/335_3152_32014_xx_ba00011226-bv00309183/xx-criteria.html"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 50,
                    "pages": 3
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.currys.co.uk/gbuk/freestanding-microwaves/cooking/microwaves/335_3152_32014_xx_ba00011226-bv00309183/xx-criteria.html"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Freestanding microwaves"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });


    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.currys.co.uk/gbuk/freestanding-microwaves/cooking/microwaves/335_3152_32014_xx_ba00011226-bv00309183/xx-criteria.html"
        }

        // Change to be the number of products on the page
        var x = 20;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'KENWOOD K25MSS11 Solo Microwave - Black & Stainless Steel';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '59.99',
                    url: 'http://www.currys.co.uk/gbuk/household-appliances/cooking/microwaves/kenwood-k25mss11-solo-microwave-black-stainless-steel-07677905-pdt.html',
                    image: 'http://brain-images.cdn.dixons.com/5/0/07677905/g_07677905.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'RUSSELL HOBBS RHMM701B Solo Microwave - Black';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '54.99',
                    url: 'http://www.currys.co.uk/gbuk/household-appliances/cooking/microwaves/russell-hobbs-rhmm701b-solo-microwave-black-10154333-pdt.html',
                    image: 'http://brain-images.cdn.dixons.com/3/3/10154333/g_10154333.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
