var should = require('should');
var Structure = require('../lib/structure.js')

describe('www.cyclegear.com', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.cyclegear.com/gear/bilt-heat-out-helmet-liner"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Bilt HEAT-OUT Helmet Liner",
                    "price": "4.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://www.cyclegear.com/_a/product_images/0194/9858/heatout_helmet_liner_750x750.jpg',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.cyclegear.com/motorcycle-boots#page=1"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 96,
                    "pages": 6
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.cyclegear.com/motorcycle-boots#page=1"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Motorcycle Boots"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.cyclegear.com/hydration"
        }

        // Change to be the number of products on the page
        var x = 67;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Fox Racing Low Pro Hydration Pack';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '54.95',
                    url: 'https://www.cyclegear.com/accessories/fox-racing-low-pro-hydration-pack-2015',
                    image: 'https://www.cyclegear.com/_a/product_images/0096/9090/fox_racing_low_pro_hydration_pack_black.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'OGIO Dakar 100 Hydration Backpack';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '109.99',
                    url: 'https://www.cyclegear.com/accessories/ogio-dakar-100-hydration-backpack-stealth',
                    image: 'https://www.cyclegear.com/images/sites/cycle_gear/support/product_images/missing_300x300.png',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
