var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.cymax.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.cymax.com/Glass-Top-Computer-Desk-800804.htm",
            "html": cache.load("https://www.cymax.com/Glass-Top-Computer-Desk-800804.htm")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Coaster Glass Top Computer Desk in Nickel",
                    "price": "191.99",
                    "url": 'https://www.cymax.com/Glass-Top-Computer-Desk-800804.htm',
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://media.cymaxstores.com/Images/699/1404767-L.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    });

    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.cymax.com/Dining-Chairs--C635.htm",
            "html": cache.load("https://www.cymax.com/Dining-Chairs--C635.htm")
        }

        // Change to be the number of products on the page
        var x = 64;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Abbyson Living Miiko Dining Chair';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '250.99',
                    url: 'https://www.cymax.com/Abbyson-Living-Miiko-Dining-Chair-in-Gray-BR-DC-2581227-GRY.htm',
                    image: 'https://media.cymaxstores.com/Images/1453/650356-1-M.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Bush Business Series A 4-Piece Corner Computer Desk in Hansen Cherry';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '1104.99',
                    url: 'https://www.cymax.com/Bush-Series-A-4-Piece-Wood-Corner-Computer-Desk-in-Hansen-Cherry-WC94428-PKG1.htm',
                    image: 'https://media.cymaxstores.com/Images/3830/435673-M.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

        describe('Search page pagination', function () {

            var payload = {
                "pageType": "pagination",
                "url": "https://www.tesco.com/direct/home-electrical/batteries/cat3375667.cat?catId=4294731032",
                "html": cache.load("https://www.tesco.com/direct/home-electrical/batteries/cat3375667.cat?catId=4294731032")
            }

            it('should be true when number of items and pages correct', function (done) {

                Structure(payload, function (err, pagination) {
                    var expected = JSON.stringify({
                        "items": 60,
                        "pages": 1,
                        "url": "https://www.tesco.com/direct/home-electrical/batteries/cat3375667.cat?catId=4294731032"
                    })
                    JSON.stringify(pagination).should.equal(expected);
                    done()
                })
            });

        });

        describe('Search page category', function () {

            var payload = {
                "pageType": "category",
                "url": "http://www.tesco.com/direct/home-electrical/electrical-accessories/cat3376649.cat?icid=homeelectrical_flyoutlink_BatteriesElectricalAccessories",
                "html": cache.load("http://www.tesco.com/direct/home-electrical/electrical-accessories/cat3376649.cat?icid=homeelectrical_flyoutlink_BatteriesElectricalAccessories")
            }

            it('should be true when the category is correct', function (done) {

                Structure(payload, function (err, category) {
                    var expected = JSON.stringify({
                        "name": "Electrical Accessories",
                        "url": "http://www.tesco.com/direct/home-electrical/electrical-accessories/cat3376649.cat?icid=homeelectrical_flyoutlink_BatteriesElectricalAccessories"
                    })
                    JSON.stringify(category).should.equal(expected);
                    done()
                })
            });

        });

    });


