var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')


describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.dealsplus.com/All-Electronics_deals/p_omaker-adjustable-car-phone-mount-holder-allows-for",
            "html": cache.load("https://www.dealsplus.com/All-Electronics_deals/p_omaker-adjustable-car-phone-mount-holder-allows-for")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Omaker Adjustable Car Phone Mount Holder Allows for SmartPhone Removal Including Iphone 7/6s/6: Cell Phones & Accessories",
                    "price": "4.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://img.dealspluscdn.com/ai/0_0/dealimage/20000/6882000/6882757_1479948121.jpg?crop=0',
                    "country": 'us',
                    "description": 'Code: AZ4TUNKZ'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    /*describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : ""
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });*/

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.dealsplus.com/All-Electronics_deals",
            "html": cache.load("https://www.dealsplus.com/All-Electronics_deals")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 30,
                    "pages": 5,
                    "url" : "https://www.dealsplus.com/All-Electronics_deals"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.dealsplus.com/All-Electronics_deals",
            "html": cache.load("https://www.dealsplus.com/All-Electronics_deals")
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Best Electronics Deals",
                    "url" : "https://www.dealsplus.com/All-Electronics_deals"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.dealsplus.com/Toys_deals",
            "html": cache.load("https://www.dealsplus.com/Toys_deals")
        }

        // Change to be the number of products on the page
        var x = 34;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'All American Eagle Outfitters Clearance Under $20';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '1.99',
                    url: 'https://www.dealsplus.com/Women_deals/p_all-american-eagle-outfitters-clearance-under',
                    image: 'https://img.dealspluscdn.com/ai/184x184/dealimage/20000/7226000/7226932_1498803026.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Martha Stewart Collection Bath Towels from $2.99 (Various Styles)';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '2.99',
                    url: 'https://www.dealsplus.com/Bed-and-Bath_deals/p_martha-stewart-collection-bath-towels-from-99-various',
                    image: 'https://img.dealspluscdn.com/ai/184x184/dealimage/20000/7226000/7226173_1498757223.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
