var should = require('should');
var Structure = require('../lib/structure.js')


describe('www.debenhams.com', function() {

    before(function()  {
        this.timeout(5000);
    })


    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.debenhams.com/toys/action-toys-playsets"
        }

        it('should return 63 products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(66);
                done()
            })
        });

        it('last product should be Pokemon Challenge', function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: 'Spin Master Rip Force Glider',
                    price: '8.40',
                    url: 'http://www.debenhams.com/webapp/wcs/stores/servlet/prod_10701_10001_171040561999_-1',
                    image: 'http://debenhams.scene7.com/is/image/Debenhams/171040561999?$PSPNew$',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

        it('first product should be a Batman vs Superman', function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: 'Batman v Superman: Dawn of Justice Kryptonite Strike Blaster',
                    price: '15.00',
                    url: 'http://www.debenhams.com/webapp/wcs/stores/servlet/prod_10701_10001_171060635699_-1',
                    image: 'http://debenhams.scene7.com/is/image/Debenhams/171060635699?$PSPNew$',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

    });


    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.debenhams.com/webapp/wcs/stores/servlet/prod_10701_10001_171070947699_-1"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Thunderbirds POD Set",
                    "price": "20.00",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://debenhams.scene7.com/is/image/Debenhams/171070947699?$V7PdpLarge$',
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });
    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.debenhams.com/toys/action-toys-playsets"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Action toys & playsets"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });
    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.debenhams.com/toys/action-toys-playsets"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 210,
                    "pages": 10
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });


    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.debenhams.com/webapp/wcs/stores/servlet/prod_10701_10001_171060731299_-1"
        }

        it('should return false when product is out of stock', function(done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Playmobil Inflatable Boat with Explorers - 5559",
                    "price": "",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "http://debenhams.scene7.com/is/image/Debenhams/171060731299?$V7PdpLarge$",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        })

        describe('Checking to see if no pagination works', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.debenhams.com/"
        }

        it('should return 0 items and null pages', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": null,
                    "pages": null
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    })

});
