var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function () {

    this.timeout(10000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "http://www.decathlon.co.uk/w200-s-timer-watch-green-blue-id_8239777.html",
            "html" : cache.load("http://www.decathlon.co.uk/w200-s-timer-watch-green-blue-id_8239777.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "GEONAUTE W200 S Timer Watch - Green & Blue",
                    "price": "9.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://www.decathlon.co.uk//media/823/8239777/big_f06a14dce42c48ecb75fb80ffb4ddd2f.jpg',
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });
    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "http://www.decathlon.co.uk/C-813502-sports-watches",
            "html" : cache.load("http://www.decathlon.co.uk/C-813502-sports-watches")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Sports Watches",
                    "url": "http://www.decathlon.co.uk/C-813502-sports-watches"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.decathlon.co.uk/C-881082-badminton-bags",
            "html" : cache.load("https://www.decathlon.co.uk/C-881082-badminton-bags")
        }

        // Change to be the number of products on the page
        var x = 21;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'GEONAUTE ONmove 50 GPS watch';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '49.99',
                    url: 'https://www.decathlon.co.uk/onmove-50-gps-watch-id_8301689.html',
                    image: 'https://www.decathlon.co.uk/media/830/8301689/classic_9b791d194ed745cbb0ede8796015d6e3.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'GEONAUTE ONmove 120 GPS watch';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '59.99',
                    url: 'https://www.decathlon.co.uk/onmove-120-gps-watch-id_8374050.html',
                    image: 'https://www.decathlon.co.uk/media/837/8374050/classic_936972191eb04b72ae382bf7f720d0c0.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
