    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.designtoscano.com/search.do?query=design&cx=0&sortby=bestSellersAscend&page=all"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 750,
                    "pages": 9
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.designtoscano.com/search.do?query=design&cx=0&sortby=bestSellersAscend&page=all"
        }

        // Change to be the number of products on the page
        var x = 633;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = "Heaven's Angel Cast Bronze Garden Statue";
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '2750.00',
                    url: 'http://www.designtoscano.com/product/heaven%26apos-s+angel+cast+bronze+garden+statue+-+kw57989.do?sortby=bestSellers&refType=&from=Search',
                    image: 'http://ii.designtoscano.com/fcgi-bin/iipsrv.fcgi?FIF=/images/toscano//source/KW57989_1.tif&wid=200&cvt=jpeg',
                    country: 'us' //ADD COUNTRY HERE PLEASE $ = us £ = uk //
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Cascading Bamboo Sculptural Fountain';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '349.00',
                    url: 'http://www.designtoscano.com/product/cascading+bamboo+sculptural+fountain+-+ss8416.do?sortby=bestSellers&refType=&from=Search',
                    image: 'http://ii.designtoscano.com/fcgi-bin/iipsrv.fcgi?FIF=/images/toscano//source/SS8416_1.tif&wid=200&cvt=jpeg',
                    country: 'us' //ADD COUNTRY HERE PLEASE $ = us £ = uk //
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

