var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function() {
    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.dillards.com/p/cuisinart-stainless-steel-pulp-control-citrus-juicer/502691820?di=03547769_zi&categoryId=915&facetCache=pageSize%3D96%26beginIndex%3D0%26orderBy%3D1",
            "html": cache.load("http://www.dillards.com/p/cuisinart-stainless-steel-pulp-control-citrus-juicer/502691820?di=03547769_zi&categoryId=915&facetCache=pageSize%3D96%26beginIndex%3D0%26orderBy%3D1")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Cuisinart Stainless Steel Pulp-Control Citrus Juicer",
                    "price": "30.00",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://dimg.dillards.com/is/image/DillardsZoom/03547769_zi?$ecom880x1020$',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.dillards.com/p/lancome-artliner-precision-point-eyeliner/525217?di=00012384_zi_azur&categoryId=420&facetCache=pageSize%3D96%26beginIndex%3D0%26orderBy%3D1%26emphasize%3D05293679",
            "html": cache.load("https://www.dillards.com/p/lancome-artliner-precision-point-eyeliner/525217?di=00012384_zi_azur&categoryId=420&facetCache=pageSize%3D96%26beginIndex%3D0%26orderBy%3D1%26emphasize%3D05293679")
        }

        it('should be false when product is no longer available', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Roundtree & Yorke Microfiber Easy-Care Pleated Expander Dress Pants",
                    "price": null,
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'http://dimg.dillards.com/is/image/DillardsZoom/03627069_zi_light_khaki?$ecom880x1020$',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.dillards.com/c/accessories-watches",
            "html": cache.load("http://www.dillards.com/c/accessories-watches")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 96,
                    "pages": 8,
                    "url" : "http://www.dillards.com/c/accessories-watches"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.dillards.com/c/home-kitchen-small-appliances",
            "html": cache.load("https://www.dillards.com/c/home-kitchen-small-appliances")
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Small Kitchen Appliances",
                    "url" : "http://www.dillards.com/c/home-kitchen-small-appliances"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.dillards.com/c/home-kitchen-small-appliances",
            "html": cache.load("https://www.dillards.com/c/home-kitchen-small-appliances")
        }

        // Change to be the number of products on the page
        var x = 7;
        it('should return ' + x + ' numer of products', function () {
            let result = Structure(payload)
            result.length.should.equal(x);
        });

        var firstProductName = 'Critters Go For A Ride 100 Large Piece Jigsaw Puzzle';
        it('first product should be ' + firstProductName, function() {

            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '4.99',
                url: 'http://www.bitsandpieces.com/product/critters-go-for-a-ride-100-large-piece-jigsaw-puzzle-42777',
                image: 'https://s3.amazonaws.com/cdn.bitsandpieces.com/images/160/42777.jpg',
                country: 'us'
            })
            
            let result = Structure(payload)

            var first = result.shift()
            
            JSON.stringify(first).should.equal(expected)
        })

        var lastProductName = 'Soccer Sliding Wooden Puzzle';
        it('last product should be ' + lastProductName, function () {
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '14.99',
                url: 'http://www.bitsandpieces.com/product/soccer-sliding-wooden-puzzle',
                image: 'https://s3.amazonaws.com/cdn.bitsandpieces.com/images/160/43952.jpg',
                country: 'us'
            })

            const result = Structure(payload);

            var last = result.pop()
            JSON.stringify(last).should.equal(expected)
        })

    }); 

});
