var should = require('should');
var Structure = require('../lib/structure.js')


describe('www.discountschoolsupply.com', function() {

    before(function()  {
        this.timeout(5000);
    })


    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.discountschoolsupply.com/Product/ProductList.aspx?category=758&src=categorynavigation"
        }

        it('should return 24 products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(24);
                done()
            })
        });

        it('last product should be a White Colorations® Simply Washable Tempera Paint, 1 Gallon', function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: 'HighScope® Colorations® Shape Makers - Set of 24',
                    price: '17.99',
                    url: 'http://www.discountschoolsupply.com/Product/ProductDetail.aspx?product=33311&Category=758',
                    image: 'http://di5cp8ixdolg4.cloudfront.net/images/products/33311.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

        it('first product should be Colorations® Classic Colors Best Value Dough - 18 lbs.', function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: 'Colorations® Classic Colors Best Value Dough - 18 lbs.',
                    price: '33.99',
                    url: 'http://www.discountschoolsupply.com/Product/ProductDetail.aspx?product=876&Category=758',
                    image: 'http://di5cp8ixdolg4.cloudfront.net/images/products/00876.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.discountschoolsupply.com/Product/ProductList.aspx?Category=244&src=categorynavigation"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 48,
                    "pages": 24
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.discountschoolsupply.com/Product/ProductList.aspx?Category=244&src=categorynavigation"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Infant & Toddler"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });


    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.discountschoolsupply.com/Product/ProductDetail.aspx?product=27627&Category=244"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Soft Touch Baby Farm Animals - Set of 6",
                    "price": "13.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://di5cp8ixdolg4.cloudfront.net/images/products/27627b.jpg',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });


    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should return false when product is out of stock', function(done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     })

    // })

});
