var should = require('should');
var Structure = require('../lib/structure.js')

describe('www.discountwatchstore.com', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.discountwatchstore.com/products/casio-bga180-9b-womens-baby-g-tide-graph-ana-digi-white-dial-yellow-resin-strap-alarm-watch.html"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Casio BGA180-9B Women's Baby-G Tide Graph Ana-Digi White Dial Yellow Resin Strap Alarm Watch",
                    "price": "79.00",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://cdn6.bigcommerce.com/s-wqbx9iwm/products/23567/images/415063/BGA180-9B__06927.1468864772.600.600.jpg?c=2',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.discountwatchstore.com/Casio-Watches_c_44.html"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 32,
                    "pages": 4
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.discountwatchstore.com/Casio-Watches_c_44.html"
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Casio Watches"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.discountwatchstore.com/collections/anne-klein-watches"
        }

        // Change to be the number of products on the page
        var x = 28;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Anne Klein 10-9168BMDD Women\'s Blue Dial Blue Denim Fabric & Brown Leather Strap Watch';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '49.90',
                    url: 'https://www.discountwatchstore.com/collections/anne-klein-watches/products/watches-anne-klein-10-9168bmdd',
                    image: 'https://cdn.shopify.com/s/assets/no-image-2048-5e88c1b20e087fb7bbe9a3771824e743c244f437e4f8ba93bbf7b11b53f7824c_500x.gif',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Anne Klein 2524CMHN Women\'s MOP Dial Horn Resin & Yellow Steel Bangle Bracelet Watch';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '50.01',
                    url: 'https://www.discountwatchstore.com/collections/anne-klein-watches/products/watches-anne-klein-2524cmhn',
                    image: 'https://cdn.shopify.com/s/files/1/1670/0573/products/2524CMHN_2e58c8f0-37d5-42ed-95f3-4b6a99eb9087_500x.jpg?v=1484705682',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
