var should = require('should');
var Structure = require('../lib/structure.js');
let cache = require('../lib/cache');

describe('www.disneystore.co.uk', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.disneystore.co.uk/spider-man-talking-action-figure-411011527361.html?cgid=marvelaf"
        }

        xit('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Spider-Man Talking Action Figure",
                    "price": "22.95",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://s7d9.scene7.com/is/image/DisneyStoreES/411011527361?$yetidetail$&defaultImage=no%20image-image_uk',
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.disneystore.co.uk/marvel/marvel-toys/marvel-action-figures#/page/2"
        }

        xit('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Marvel Action Figures"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });
    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null,
    //                 "image": '',
    //                 "country": ''
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.shopdisney.co.uk/disney/adults",
            "html": cache.load("https://www.shopdisney.co.uk/disney/adults")
        }

        // Change to be the number of products on the page
        var x = 48;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Spider-Man Talking Action Figure';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '22.95',
                    url: 'http://www.disneystore.co.uk/spider-man-talking-action-figure-411011527361.html?cgid=marvelaf',
                    image: 'http://s7d9.scene7.com/is/image/DisneyStoreES/411011527361?$yeti3UPList$&defaultImage=no%20image-image_uk',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Falcon Titan Hero 12\'\' Action Figure, Captain America: Civil War';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '22.95',
                    url: 'http://www.disneystore.co.uk/falcon-titan-hero-12-action-figure-captain-america-civil-war-461014158680.html?cgid=marvelaf',
                    image: 'http://s7d9.scene7.com/is/image/DisneyStoreES/461014158680?$yeti3UPList$&defaultImage=no%20image-image_uk',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
