var should = require('should');
var Structure = require('../lib/structure.js')

describe('www.drsfostersmith.com', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.drsfostersmith.com/product/prod_display.cfm?c=5163+5186+25033&pcatid=25033"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Pond Logic® TrueRock™ Faux Rock Skimmer & Waterfall Covers",
                    "price": "89.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://www.drsfostersmith.com/images/Categoryimages/normal/p-81419-70391-002-4.jpg',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.drsfostersmith.com/dog-supplies/dog-toys/ps/c/3307/3"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 23,
                    "pages": 13
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

     describe('Not a search page', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.drsfostersmith.com/product/prod_display.cfm?c=3307+3+533&pcatid=533"
        }

        it('should return null items and pages', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": null,
                    "pages": null
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.drsfostersmith.com/fish-supplies/aquarium-air-pump/ps/c/3578/3669"
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Air Pumps & Air Stones"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.drsfostersmith.com/pond-supplies/pond-filters/ps/c/5163/5186"
        }

        // Change to be the number of products on the page
        var x = 23;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Laguna PowerClear Multi All In One Pond Filter';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '199.99',
                    url: 'http://www.drsfostersmith.com/product/prod_display.cfm?c=5163+5186+33049&pcatid=33049',
                    image: 'http://www.drsfostersmith.com/images/Categoryimages/thumbnail/th-97371-89331K_001-pond.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'The Leaf Basket';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '99.99',
                    url: 'http://www.drsfostersmith.com/product/prod_display.cfm?c=5163+5186+5342&pcatid=5342',
                    image: 'http://www.drsfostersmith.com/images/Categoryimages/thumbnail/th_161228_15354Z.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
