var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function () {

    this.timeout(10000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "http://www.drugsupplystore.com/bilberry-320mg-conc-capsule-60ct-sundown.html",
            "html": cache.load("http://www.drugsupplystore.com/bilberry-320mg-conc-capsule-60ct-sundown.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Bilberry 320mg Conc Capsule 60ct Sundown",
                    "price": "3.51",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "http://cdn6.bigcommerce.com/s-79bvd/products/9774/images/19602/100463051__75856.1449793961.386.513.jpg?c=2",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "http://www.drugsupplystore.com/deals/",
            "html": cache.load("http://www.drugsupplystore.com/deals/")
        }

        // Change to be the number of products on the page
        var x = 30;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });
        describe('Search page pagination', function () {

            var payload = {
                "pageType": "pagination",
                "url": "http://www.drugsupplystore.com/vitamins-and-minerals/",
                "html": cache.load("http://www.drugsupplystore.com/vitamins-and-minerals/")
            }

            it('should be true when number of items and pages correct', function (done) {

                Structure(payload, function (err, pagination) {
                    var expected = JSON.stringify({
                        "items": 32,
                        "pages": 4,
                        "url": "http://www.drugsupplystore.com/vitamins-and-minerals/"
                    })
                    JSON.stringify(pagination).should.equal(expected);
                    done()
                })
            });

        });
        describe('Search page category', function () {

            var payload = {
                "pageType": "category",
                "url": "http://www.drugsupplystore.com/deals/",
                "html": cache.load("http://www.drugsupplystore.com/deals/")
            }

            it('should be true when number of items and pages correct', function (done) {

                Structure(payload, function (err, category) {
                    var expected = JSON.stringify({
                        "name": "Vitamins and Minerals",
                        "url": "http://www.drugsupplystore.com/vitamins-and-minerals/"
                    })
                    JSON.stringify(category).should.equal(expected);
                    done()
                })
            });

        });

        var firstProductName = 'Bee Plush Animal kids Backpack';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '2.14',
                    url: 'http://www.drugsupplystore.com/bee-plush-animal-kids-backpack/',
                    image: 'http://cdn8.bigcommerce.com/s-79bvd/images/stencil/500x500/products/5535/11092/bumblebee__06777.1407800816.jpg?c=2',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Touch Lanterns indoor outdoor portable accent lights';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '2.14',
                    url: 'http://www.drugsupplystore.com/touch-lanterns-indoor-outdoor-portable-accent-lights/',
                    image: 'http://cdn8.bigcommerce.com/s-79bvd/images/stencil/500x500/products/6060/12134/5248918125__02655.1407800897.jpg?c=2',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
