var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.duncanstoys.com/cra-z-art-shimmer-and-sparkle-super-cra-z-loom"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Cra-z-art Shimmer And Sparkle Super Cra-z-loom",
                    "price": "20.49",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://www.duncanstoys.com/media/catalog/product/cache/3/thumbnail/1200x1200/9df78eab33525d08d6e5fb8d27136e95/c/r/cra-z-art-shimmer-and-sparkle-super-cra-z-loom-1.jpg',
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });
    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.duncanstoys.com/arts-and-crafts/beads-jewellery"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Beads & Jewellery"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.duncanstoys.com/arts-and-crafts/beads-jewellery"
        }

        // Change to be the number of products on the page
        var x = 5;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Beados Themed Refill Pack [Design will Vary]';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '7.99',
                    url: 'https://www.duncanstoys.com/quickview/index/view/id/27464',
                    image: 'https://www.duncanstoys.com/media/catalog/product/cache/3/small_image/250x/9df78eab33525d08d6e5fb8d27136e95/b/e/beados-themed-refill-pack-2.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Cra-z-loom Neon Refill';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '3.48',
                    url: 'https://www.duncanstoys.com/quickview/index/view/id/25853',
                    image: 'https://www.duncanstoys.com/media/catalog/product/cache/3/small_image/250x/9df78eab33525d08d6e5fb8d27136e95/c/r/cra-z-loom-neon-refill-1.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
