var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.electricshopping.com/aeg-sb7500-7-series-blender-with-truflow-titanium-blades/"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "AEG SB7500 7 Series Blender with TruFlow Titanium Blades",
                    "price": "137.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://cdn3.bigcommerce.com/s-p2ed6r2j/products/11328/images/13348/AEGSB7500__01509.1461156213.450.559.jpg?c=2',
                    "country": 'uk',
                    "description": 'TruFlow Titanium blades stay sharp and enable high performance and fast blending.   900 W motor makes light work of fruit, ice or nuts to make you the ideal smoothies TruFlow Titanium blades stay sharp and enable high performance and fast blending Five pre-set programs include variable speed setting (high/low), ice crush, smooth and pulse 1.65 L glass jug can blend large portions and is dishwasher safe LED backlighting on controls'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null,
    //                 "image": '',
    //                 "country": '',
    //                 "description": ''
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.electricshopping.com/kitchen-cooking/food-preparation/blenders/table-top-blenders/"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 25,
                    "pages": 2
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page pagination URL Two', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.electricshopping.com/kitchen-cooking/hot-cold-drinks/kettles/"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 25,
                    "pages": 4
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })


    describe('Not a Search Page', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.electricshopping.com/"
        }

        it('should should pass when items and pages are null', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": null,
                    "pages": null
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.electricshopping.com/kitchen-cooking/food-preparation/blenders/table-top-blenders/"
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Table Top Blenders"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.electricshopping.com/kitchen-cooking/food-preparation/blenders/table-top-blenders/"
        }

        // Change to be the number of products on the page
        var x = 25;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'AEG SB7500 7 Series Blender with TruFlow Titanium Blades';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '137.99',
                    url: 'http://www.electricshopping.com/aeg-sb7500-7-series-blender-with-truflow-titanium-blades/',
                    image: 'http://cdn3.bigcommerce.com/s-p2ed6r2j/products/11328/images/13348/AEGSB7500__01509.1461156213.210.210.jpg?c=2',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Prestige Stainless steel 600ml Blender and Drink Holder in Black';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '59.77',
                    url: 'http://www.electricshopping.com/prestige-stainless-steel-600ml-blender-and-drink-holder-in-black/',
                    image: 'http://cdn3.bigcommerce.com/s-p2ed6r2j/products/11267/images/13156/pr59903__55813.1442920808.210.210.jpg?c=2',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
