var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.elementoutfitters.com/Orvis-Men-s-Silver-Sonic-Convertible-Top-Waders-p/orv5h5e01.htm"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Orvis Men's Silver Sonic Convertible-Top Waders",
                    "price": "278.95",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://cdn3.volusion.com/jeftr.uywtr/v/vspfiles/photos/ORV5H5E01-2T.jpg?1397418672',
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });


    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.elementoutfitters.com/Footwear-and-Outdoor-Shoes-s/2931.htm"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 300,
                    "pages": 14
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })
     describe('Not a search page', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.elementoutfitters.com/Keen-Men-s-Dallas-Wellington-Boot-p/kee1007043.htm"
        }

        it('should return null items and pages', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": null,
                    "pages": null
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.elementoutfitters.com/Footwear-and-Outdoor-Shoes-s/2931.htm"
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Outdoor Shoes"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.elementoutfitters.com/Footwear-and-Outdoor-Shoes-s/2931.htm"
        }

        // Change to be the number of products on the page
        var x = 30;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = "Sorel Women's Joan of Arctic Wedge Mid Boots";
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '249.95',
                    url: 'http://www.elementoutfitters.com/Sorel-Women-s-Joan-of-Arctic-Wedge-Mid-Boots-p/sor1627141.htm',
                    image: 'http://cdn3.volusion.com/jeftr.uywtr/v/vspfiles/photos/SOR1627141-1.jpg?1473849949',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = "Dansko Women's Pro XP Shoes";
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '149.95',
                    url: 'http://www.elementoutfitters.com/Dansko-Women-s-Pro-XP-Shoes-p/dsk3912360.htm',
                    image: 'http://cdn3.volusion.com/jeftr.uywtr/v/vspfiles/photos/DSK3912360-1.jpg?1413968996',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
