var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function () {

    this.timeout(10000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "http://www.euroffice.co.uk/product/mesh-waste-bin-lightweight-sturdy-scratch-resistant-w275xh350mm-black/AAN183"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (inStockPage) {
                var expected = JSON.stringify({
                    "name": "Mesh Waste Bin Lightweight Sturdy Scratch Resistant W275xH350mm Black",
                    "price": "8.19",
                    "url": payload.url,
                    "inStock": 'UNCHECKED',
                    "stockLevel": null,
                    "image": 'http://powercdn.s3.amazonaws.com/4_2014/678d831b-e342-43e8-9310-4f2ac3e32b0a.jpg',
                    "country": 'uk',
                    "description": 'Lightweight and sturdy mesh waste binLarge capacity allows emptying less oftenCorrosion and scratch-resistantModern and stylishDiameter: 235mm (base), 275mm (top)Height: 350mmBlackUnbranded Product - Design may vary',
                    "sku": 'AAN183'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null,
    //                 "image": '',
    //                 "country": '',
    //                 "description": ''
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function () {

        var payload = {
            "pageType": "pagination",
            "url": "http://www.euroffice.co.uk/category/office-supplies/desk-accessories"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function (pagination) {
                var expected = JSON.stringify({
                    "items": 100,
                    "pages": 4
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Not a search page', function () {

        var payload = {
            "pageType": "pagination",
            "url": "http://www.euroffice.co.uk/product/5-star-office-standard-clipboard-with-pvc-cover-foolscap-black/AAN779"
        }

        it('should return null items and pages', function (done) {

            Structure(payload, function (pagination) {
                var expected = JSON.stringify({
                    "items": null,
                    "pages": null
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "http://www.euroffice.co.uk/category/office-supplies/desk-accessories"
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function (category) {
                var expected = JSON.stringify({
                    "name": "Desk Accessories"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.euroffice.co.uk/category/computing/computer-accessories/computer-headsets"
        }

        // Change to be the number of products on the page
        var x = 7;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function (result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Logitech H340 Headset USB Lightweight with Noise-cancelling Microphone Ref 981-000475';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '40.80',
                    url: 'https://www.euroffice.co.uk/product/logitech-h340-headset-usb-lightweight-with-noise-cancelling-microphone-ref-981-000475/AAW034',
                    image: 'https://powercdn.s3.amazonaws.com/4_2015/e0d68f40-56bf-4b15-9775-b317efc7d3b7.jpg',
                    country: 'uk',
                    sku: 'AAW034'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Jabra BIZ 2300 Duo Noise Cancelling Headset Ref 2309-820-104';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '127.91',
                    url: 'https://www.euroffice.co.uk/product/jabra-biz-2300-duo-noise-cancelling-headset-ref-2309-820-104/ADL967',
                    image: 'https://powercdn.s3.amazonaws.com/12_2014/9861c25b-3150-4491-9e0e-d2ae94a450c1.JPG',
                    country: 'uk',
                    sku: 'ADL967'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
