var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')


describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.evanscycles.com/exposure-strada-1200-with-daybright-front-light-1200-lumen-EV278964",
            "html": cache.load("https://www.evanscycles.com/exposure-strada-1200-with-daybright-front-light-1200-lumen-EV278964")

        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "FWE USB Rechargeable 30 Lumen Rear Light",
                    "price": "19.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://static.evanscycles.com/production/accessories/lights/product-image/484-319/fwe-usb-rechargeable-30-lumen-rear-light-black-EV277494-8500-1.jpg',
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });
    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.evanscycles.com/accessories/lights_c"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Bike Lights"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });
    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.evanscycles.com/bikes/road-bikes_c?page=1&sort=most_popular&productsPerPage=24&contentLayout=grid"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 24,
                    "pages": 20
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

     describe('Not a search page', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.evanscycles.com/genesis-croix-de-fer-20-2016-cyclocross-bike-EV258263"
        }

        it('should return null items and pages', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": null,
                    "pages": null
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.evanscycles.com/bike-accessories/bike-lights_c",
            "html": cache.load("https://www.evanscycles.com/bike-accessories/bike-lights_c")
        }

        // Change to be the number of products on the page
        var x = 24;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'FWE USB Rechargeable 30 Lumen Rear Light';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '19.99',
                    url: 'http://www.evanscycles.com/fwe-usb-rechargeable-30-lumen-rear-light-EV277494',
                    image: 'https://static.evanscycles.com/production/accessories/lights/product-image/220-145/fwe-usb-rechargeable-30-lumen-rear-light-black-EV277494-8500-1.jpg?Lo0P=b3b227237a5025159c5771faa4b39390Dc',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Moon Aerolite Helmet Light';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '33.99',
                    url: 'http://www.evanscycles.com/moon-aerolite-helmet-light-EV246500',
                    image: 'https://static.evanscycles.com/production/accessories/lights/product-image/220-145/moon-aerolite-helmet-light-black-EV246500-8500-4.jpg?Lo0P=ee74524321f1c830d5c6666fa6caaa5bDc',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
