var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.fabric.com/buy/0260771/faux-fur-hi-low-rabbit-rust-brown"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Faux Fur Hi/Low Rabbit Rust/Brown",
                    "price": "29.98",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://d2d00szk9na1qq.cloudfront.net/Product/ca127606-4e0e-30e6-e043-2a59000a2831/Images/Large_0260771.jpg',
                    "country": 'us',
                    "description": "Spoil yourself with this exquisite long hair faux fabric. From Shannon Fabrics, this fur has alternating 1/2'' to 1'' pile, a luxurious hand and a soft subtle sheen just like the real thing! Make gorgeous jackets, coats, wraps, fashion accessories, pillows, throws and more!"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.fabric.com/buy/as-865/minky-cuddle-zebra-black"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Minky Cuddle Zebra Black",
                    "price": "13.98",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'https://d2d00szk9na1qq.cloudfront.net/Product/14fccc65-b93f-4aa1-8219-dfd9c657fd48/Images/Large_AS-865.jpg',
                    "country": 'us',
                    "description": "This extremely soft and cuddly fabric has a smooth minky surface with an animal print, 3 mm pile, 380 grams and is perfect for creating stuffed animals, baby accessories, blankets, throws, pillows and more! Colors include black and ivory."
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.fabric.com/apparel-fashion-fabric-animal-print-fabric.aspx"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 60,
                    "pages": 4
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page pagination, second URL', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.fabric.com/home-decor-fabric-designer-home-decor-shop.aspx?page=4"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 60,
                    "pages": 166
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Not a real Search Page', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.fabric.com/buy/0350747/richloom-faux-leather-reptile-black"
        }

        it('should pass when the pages and items are null', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": null,
                    "pages": null
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })


    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.fabric.com/apparel-fashion-fabric-animal-print-fabric.aspx"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Animal Print Fabric"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.fabric.com/apparel-fashion-fabric-animal-print-fabric.aspx"
        }

        // Change to be the number of products on the page
        var x = 60;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Minky Pony Soft Cuddle Ivory/Brown';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '13.98',
                    url: 'https://www.fabric.com/buy/0386406/minky-pony-soft-cuddle-ivory-brown',
                    image: 'https://d2d00szk9na1qq.cloudfront.net/Product/330c47b1-4d74-4dbe-8a14-bb6666961b0f/Images/Medium_0386406.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Designer Denim Stretch Twill Gold Scales';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '7.98',
                    url: 'https://www.fabric.com/buy/0455047/designer-denim-stretch-twill-gold-scales',
                    image: 'https://d2d00szk9na1qq.cloudfront.net/Product/9f87871d-0934-409c-b311-a4b938c8ae9a/Images/Medium_0455047.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
