var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.fanatics.com/2016_Stocking_Stuffers/Chicago_Cubs_2016_World_Series_Champions_Replica_Baseball_Ornament"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Chicago Cubs 2016 World Series Champions Replica Baseball Ornament",
                    "price": "9.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://images.footballfanatics.com/FFImage/thumb.aspx?i=/productImages%2f_2617000%2fff_2617700_full.jpg&w=600',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });


    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.fanatics.com/2016_stocking_stuffers?ab={wt-static_graphic}{pt-pages}{al-banner}{ct-stocking_stuffers}"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 84,
                    "pages": 512
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });


    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.fanatics.com/2016_stocking_stuffers?ab={wt-static_graphic}{pt-pages}{al-banner}{ct-stocking_stuffers}"
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "2016 Stocking Stuffers"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.fanatics.com/2016_stocking_stuffers?ab={wt-static_graphic}{pt-pages}{al-banner}{ct-stocking_stuffers}"
        }

        // Change to be the number of products on the page
        var x = 72;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Chicago Cubs 2016 World Series Champions Replica Baseball Ornament';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '9.99',
                    url: 'www.fanatics.com/2016_Stocking_Stuffers/Chicago_Cubs_2016_World_Series_Champions_Replica_Baseball_Ornament',
                    image: 'http://images.footballfanatics.com/FFImage/thumb.aspx?i=/productImages/_2617000/ff_2617700_full.jpg&w=340',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Seattle Seahawks Reindeer Games & Santa 2-Pack Ornament Set';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '17.99',
                    url: 'www.fanatics.com/2016_Stocking_Stuffers/Seattle_Seahawks_Reindeer_Games_And_Santa_2-Pack_Ornament_Set',
                    image: 'http://images.footballfanatics.com/FFImage/thumb.aspx?i=/productImages/_2574000/ff_2574032_full.jpg&w=340',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
