var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.fatwallet.com/deals/sony-wireless-on-ear-stereo-headphones-mdrzx330bt-b-3246529"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Sony Wireless On-Ear Stereo Headphones (MDRZX330BT/B)",
                    "price": "44.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://static.fatwallet.com/coupon-images/logo/24/logo-1543202-24dbcd56b060ec47b7b0f596ac1e24e2.jpg',
                    "country": 'us',
                    "description": 'Best Buy has the Sony Wireless On-Ear Stereo Headphones on sale for $44.99 with free shipping. It is also available on Best Buy (via eBay).Deal Notes: This matches Amazon\'s current and lowest price ever according to Camelcamelcamel. However Amazon is currently backordered for 1-2 months. Bluetooth audio streaming with SBC and AAC support, Convenient One-touch pairing with NFCBuilt-in microphone for hands-free phone calling, Rechargeable battery for up to 30hrs music playback30mm drivers for full frequency response, Compact, swiveling on-ear ear design, USB charging cable suppliedReview: techradarRated 4.3/5 stars with 486 customer reviews.The next lowest price is $67.99 from Amazon.'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    /*describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : ""
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });*/

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.fatwallet.com/#offerlistsection"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 60,
                    "pages": 4
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.fatwallet.com/#offerlistsection"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Today's Best Deals"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.fatwallet.com/#offerlistsection"
        }

        // Change to be the number of products on the page
        var x = 60;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Sony Wireless On-Ear Stereo Headphones...';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '44.99',
                    url: 'https://www.fatwallet.com/deals/sony-wireless-on-ear-stereo-headphones-mdrzx330bt-b-3246529',
                    image: 'https://static.fatwallet.com/coupon-images/logo/24/logo-1543202-24dbcd56b060ec47b7b0f596ac1e24e2.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Military Grade Tactical Police SWAT Heavy Duty...';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '9.99',
                    url: 'https://www.fatwallet.com/deals/military-grade-tactical-police-swat-heavy-duty-3w-led-rechargeable-flashlig-3244877',
                    image: 'https://static.fatwallet.com/coupon-images/logo/d4/logo-1541009-d44e1f6b121e79ef91ea8aca8d9badb1.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
