var should = require('should');
var Structure = require('../lib/structure.js')
let cache = require('../lib/cache')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.firebox.com/Stuffed-Blobfish/p6622",
            "html": cache.load("http://www.firebox.com/Stuffed-Blobfish/p6622")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Stuffed Blobfish",
                    "price": "19.99",
                    "url": payload.url,
                    "inStock": "UNCHECKED",
                    "stockLevel": null,
                    "image": 'http://media.firebox.com/product/6622/column_grid_2/stuffed-blobfish_2784.jpg',
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null,
    //                 "image": '',
    //                 "country": '',
    //                 "description": ''
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.firebox.com/toys-and-games/t54?via=catbar&page=1",
            "html": cache.load("http://www.firebox.com/toys-and-games/t54?via=catbar&page=1")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 50,
                    "pages": 2,
                    "url" : "http://www.firebox.com/toys-and-games/t54?via=catbar&page=1"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

     describe('Not a search page', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.firebox.com/Stuffed-Blobfish/p6622",
            "html": cache.load("http://www.firebox.com/Stuffed-Blobfish/p6622")
        }

        it('should return null items and pages', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": null,
                    "pages": null,
                    "url" : "http://www.firebox.com/Stuffed-Blobfish/p6622"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.firebox.com/toys-and-games/t54?via=catbar&page=1",
            "html": cache.load("http://www.firebox.com/toys-and-games/t54?via=catbar&page=1")
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Toys & Games",
                    "url" : "http://www.firebox.com/toys-and-games/t54?via=catbar&page=1"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.firebox.com/food-and-drink/alcohol/t30?via=catbar&page=1",
            "html": cache.load("https://www.firebox.com/food-and-drink/alcohol/t30?via=catbar&page=1")
        }

        // Change to be the number of products on the page
        var x = 35;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Personalised Letterbox Wine';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '24.99',
                    url: 'http://www.firebox.com/Personalised-Letterbox-Wine/p8559',
                    image: 'http://media.firebox.com/product/8559/column_grid_4/personalised-letterbox-wine_31307.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Scorpion Vodka';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '12.99',
                    url: 'http://www.firebox.com/Scorpion-Vodka/p1468',
                    image: 'http://media.firebox.com/product/1468/column_grid_4/scorpion-vodka_22835.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
