var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.fleetfarm.com/detail/suncast-hosehandler-portable-or-wall-mount-hose-reel/0000000039664"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Suncast HoseHandler Portable or Wall-Mount Hose Reel",
                    "price": "24.89",
                    "url": "http://www.fleetfarm.com/detail/suncast-hosehandler-portable-or-wall-mount-hose-reel/0000000039664",
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://www.fleetfarm.com/products/images/medium/0000000039664.jpg',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.fleetfarm.com/detail/rain-bird-600-series-4-in-pop-up-spray-head-quarter-circle/0000000037039"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Rain Bird 600 Series 4 In. Pop-Up Spray Head-Quarter Circle",
                    "price": "2.89",
                    "url": "http://www.fleetfarm.com/detail/rain-bird-600-series-4-in-pop-up-spray-head-quarter-circle/0000000037039",
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'https://www.fleetfarm.com/products/images/medium/0000000037039.jpg',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page 2', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.fleetfarm.com/detail/banzai-aqua-blast-obstacle-course/0000000241030"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Banzai Aqua Blast Obstacle Course",
                    "price": "24.95",
                    "url": "http://www.fleetfarm.com/detail/banzai-aqua-blast-obstacle-course/0000000241030",
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'https://www.fleetfarm.com/products/images/medium/0000000241030.jpg',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.fleetfarm.com/category/toys/outdoor-play/outdoor-toys-games/_/N-3336027857"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 24,
                    "pages": 3
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.fleetfarm.com/category/toys/12-to-14-years?q=&categoryId=01695&brand=&q_facetTrail=&howMany=96"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Toys - 12 to 14 Years"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.fleetfarm.com/category/toys/outdoor-play/outdoor-toys-games/_/N-3336027857#/category/toys/outdoor-play/outdoor-toys-games/_/N-3336027857?null&_=1518002899626&Nrpp=24&No=0"
        // "url": "http://www.fleetfarm.com/browse/newArrivalsClearanceSeeAll.jsp" (SALE SEARCH PAGE)
        }

        // Change to be the number of products on the page
        var x = 24;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Goliath Phlat Ball Jr. Assorted';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '6.31',
                    url: 'http://www.fleetfarm.com/detail/goliath-phlat-ball-jr-assorted/0000000253048?bc=10873|10912|12346',
                    image: 'https://www.fleetfarm.com/images/product/0000000253048/m/1.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Plastic Bowling Set Assorted';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '7.91',
                    url: 'http://www.fleetfarm.com/detail/-plastic-bowling-set-assorted/0000000200166?bc=10873|10912|12346',
                    image: 'https://www.fleetfarm.com/images/product/0000000200166/m/1.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
