var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithDomainName', function () {

    this.timeout(5000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.focalprice.com/fashion-bags-3d-creative-printed-rainbow-pigment-men-and-women-rucksack-travel-satchel-backpack-white-150.html",
            "html": cache.load("https://www.focalprice.com/fashion-bags-3d-creative-printed-rainbow-pigment-men-and-women-rucksack-travel-satchel-backpack-white-150.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "3D Creative Printed Rainbow Pigment Men And Women Rucksack Travel Satchel Backpack - White",
                    "price": "22.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://www.focalprice.com/img/p/7/2/9/6/7296-large_default.jpg",
                    "country": 'us',
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "",
            "html": cache.load("")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.focalprice.com/fashion-bags-121",
            "html": cache.load("https://www.focalprice.com/fashion-bags-121")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Fashion Bags",
                    "url":"https://www.focalprice.com/fashion-bags-121"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

  xdescribe('Search page pagination', function () {

                var payload = {
                    "pageType": "pagination",
                    "url": "https://www.focalprice.com/best-sales",
                    "html": cache.load("https://www.focalprice.com/best-sales")
                }

                it('should be true when number of items and pages correct', function (done) {

                    Structure(payload, function (err, pagination) {
                        var expected = JSON.stringify({
                            "items": '',
                            "pages": ''
                        })
                        JSON.stringify(pagination).should.equal(expected);
                        done()
                    })
                });

            });


    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.focalprice.com/fashion-bags-121",
            "html": cache.load("https://www.focalprice.com/fashion-bags-121")
        }

        // Change to be the number of products on the page
        var x = 24;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = '3D Creative Printed Eye Pattern Men And Women Rucksack Travel Satchel Backpack - White';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '24.99',
                    url: 'https://www.focalprice.com/fashion-bags-3d-creative-printed-eye-pattern-men-and-women-rucksack-travel-satchel-backpack-white-151.html',
                    image: 'https://www.focalprice.com/img/p/7/2/9/9/7299-home_default.jpg',
                    country: 'us',
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = "Fashion Toet Purse Satchel Bag PU Leather Women's Handbags";
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '39.99',
                    url: 'https://www.focalprice.com/fashion-bags-fashion-toet-purse-satchel-bag-pu-leather-women-s-handbags-69.html',
                    image: 'https://www.focalprice.com/img/p/6/8/7/6/6876-home_default.jpg',
                    country: 'us',

                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});


