var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.footaction.com/product/model:236469/sku:77124/adidas-originals-superstar-mens/white/black/"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "adidas Originals Superstar - Men's",
                    "price": "79.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://images.footaction.com/is/image/EBFL2/77124_fr_adidas_sc7?fit=constrain,1&wid=500&hei=500&fmt=jpg',
                    "country": 'us',
                    "description": 'The adidas Originals Superstar debuted in 1969 as a pro basketball shoe, but has now become a street-wear staple. Smooth leather and/or textile upper with synthetic 3-StripesÂ® and the iconic shell toe. Rubber outsole supplies durability and traction.'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    /*describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : ""
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });*/

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.footaction.com/Shoes/_-_/N-rj?Rpp=180&cm_PAGE=180&Nr=AND%28P_RecordType%3AProduct%29"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 180,
                    "pages": 41
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.footaction.com/Shoes/_-_/N-rj?Rpp=180&cm_PAGE=180&Nr=AND%28P_RecordType%3AProduct%29"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "SHOES"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.footaction.com/Shoes/_-_/N-rj?Rpp=180&cm_PAGE=180&Nr=AND%28P_RecordType%3AProduct%29"
        }

        // Change to be the number of products on the page
        var x = 180;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Jordan True Flight - Men\'s - Black / Gold';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '139.99',
                    url: 'http://www.footaction.com/product/model:208261/sku:42964026/jordan-true-flight-mens/black/gold/',
                    image: 'http://images.footaction.com/pi/42964026/large/jordan-true-flight-mens',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'adidas Superstar 3G Slide - Men\'s - White / Black';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '29.99',
                    url: 'http://www.footaction.com/product/model:162319/sku:61951/adidas-superstar-3g-slide-mens/white/black/',
                    image: 'http://images.footaction.com/pi/61951/large/adidas-superstar-3g-slide-mens',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
